import React, { useParams, useState, useEffect } from "react";
import axios from "axios";

const EditBooksPage = () => {
  const { isbn, location } = useParams();
  const [books, setBooks] = useState([]); // All book copies
  const [commonFields, setCommonFields] = useState({
    bookName: "",
    authorName: "",
    category: "",
    isbnNumber: "",
  });

  // Fetch the books on page load
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/lms/books/list?isbn=${isbn}&location=${location}`);
        const booksData = Array.isArray(response.data) ? response.data : [];
        setBooks(response.data);
        if (response.data.length > 0) {
          const firstBook = response.data[0];
          setCommonFields({
            bookName: firstBook.bookName || "",
            authorName: firstBook.authorName || "",
            category: firstBook.category || "",
            isbnNumber: firstBook.isbnNumber || "",
          });
        }
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };
    fetchBooks();
  }, []);

  // Handle common field changes
  const handleCommonChange = (e) => {
    const { name, value } = e.target;
    setCommonFields({
      ...commonFields,
      [name]: value,
    });
  };

  // Handle individual field changes in the table
  const handleBookChange = (index, field, value) => {
    const updatedBooks = [...books];
    updatedBooks[index][field] = value;
    setBooks(updatedBooks);
  };

  // Submit common changes
  const handleCommonSubmit = async (e) => {
    e.preventDefault();
    try {
      // Apply common changes to all books
      await Promise.all(
        books.map((book) =>
          axios.put(`http://localhost:8080/lms/books/${book.bookId}`, {
            ...book,
            ...commonFields, // Overwrite with common fields
          })
        )
      );
      alert("Common changes applied successfully!");
    } catch (error) {
      console.error("Error updating books:", error);
    }
  };

  // Submit individual changes for a specific copy
  const handleIndividualSubmit = async (bookId, updatedBook) => {
    try {
      await axios.put(`http://localhost:8080/lms/books/${bookId}`, updatedBook);
      alert("Book updated successfully!");
    } catch (error) {
      console.error("Error updating book:", error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-6">Edit Books</h2>

      {/* Common Edit Section */}
      <div className="mb-8 bg-gray-100 p-4 rounded-lg">
        <h3 className="text-xl font-semibold mb-4">Edit Common Fields</h3>
        <form onSubmit={handleCommonSubmit}>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label>Book Name</label>
              <input
                type="text"
                name="bookName"
                value={commonFields.bookName}
                onChange={handleCommonChange}
                className="input input-bordered w-full"
              />
            </div>
            <div>
              <label>Author Name</label>
              <input
                type="text"
                name="authorName"
                value={commonFields.authorName}
                onChange={handleCommonChange}
                className="input input-bordered w-full"
              />
            </div>
            <div>
              <label>Category</label>
              <input
                type="text"
                name="category"
                value={commonFields.category}
                onChange={handleCommonChange}
                className="input input-bordered w-full"
              />
            </div>
            <div>
              <label>ISBN Number</label>
              <input
                type="text"
                name="isbnNumber"
                value={commonFields.isbnNumber}
                onChange={handleCommonChange}
                className="input input-bordered w-full"
              />
            </div>
          </div>
          <button type="submit" className="btn btn-success mt-4">
            Apply Changes to All Copies
          </button>
        </form>
      </div>

      {/* Individual Edit Table */}
      <table className="table-auto w-full border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="px-4 py-2">Book Id</th>
            <th className="px-4 py-2">Location</th>
            <th className="px-4 py-2">Available</th>
            <th className="px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book, index) => (
            <tr key={book.bookId}>
              <td className="px-4 py-2">{book.bookId}</td>
              <td className="px-4 py-2">
                <input
                  type="text"
                  value={book.location}
                  onChange={(e) => handleBookChange(index, "location", e.target.value)}
                  className="input input-bordered w-full"
                />
              </td>
              <td className="px-4 py-2">
                <input
                  type="checkbox"
                  checked={book.available}
                  onChange={(e) => handleBookChange(index, "available", e.target.checked)}
                />
              </td>
              <td className="px-4 py-2">
                <button
                  className="btn btn-primary"
                  onClick={() => handleIndividualSubmit(book.bookId, book)}
                >
                  Save
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EditBooksPage;
