import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import './AdminDashboard.css';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [totalPages, setTotalPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    bookName: "",
    description: "",
    authorName: "",
    isbnNumber: "",
    edition: 1,
    category: "",
    isAvailable: true,
    location: "",
    count: 1, 
  });
  const [isAddingExistingBook, setIsAddingExistingBook] = useState(false); // Track if adding count to an existing book

  const navigate = useNavigate();

  // Fetch books from the API
  const fetchBooks = async (page = 0) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:8080/lms/books/grouped?page=${page}&size=10`);
      setBooks(response.data.books);
      setTotalPages(response.data.totalPages);
      setCurrentPage(page);
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      fetchBooks(newPage);
    }
  };

  // Handle form change
  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  // Handle adding a new book or  adding copies of existing book
  const handleAddBook = async (e) => {
    try {
      // Send a single request to add multiple copies of the book
      await axios.post("http://localhost:8080/lms/books/addBooks", formData);

      // Reset form and close the form
      setFormData({
        bookName: "",
        description: "",
        authorName: "",
        isbnNumber: "",
        edition: 1,
        category: "",
        location: "",
        count: 1,
      });

      // Reload the list of books
      await fetchBooks(currentPage);

      // Show the alert after the state has updated and the books have been fetched
      alert("Books added successfully!");

      // Close the modal after submission
      setShowForm(false); 

    } catch (error) {
      console.error("Error adding book:", error);
    }
  };


  // Fetch book details by ISBN (for adding count to existing book)
  const handleLookupBook = async (isbnNumber) => {
    try {
      const response = await axios.get(`http://localhost:8080/lms/books/details/isbn/${isbnNumber}`);
      const book = response.data;

      setFormData({
        bookName: book.bookName,
        description: book.description,
        authorName: book.authorName,
        isbnNumber: book.isbnNumber,
        edition: book.edition,
        category: book.category,
        isAvailable: book.isAvailable,
        location: "", // Location is editable
        count: 1, // Default count is 1
      });
      setIsAddingExistingBook(true); // Enable adding count to an existing book
    } catch (error) {
      console.error("Error fetching book:", error);
    }
  };

  // Navigate to the EditBook page
  const handleEdit = (isbn, location) => {
    navigate(`/editBook/${isbn}/${location}`);
  };

  // Navigate to the DeleteBook page
  const handleDelete = (isbn, location) => {
    navigate(`/deleteBook/${isbn}/${location}`);
  };

  // Show the modal for adding a new book or adding count to an existing book
  const showAddBookForm = () => {
    setFormData({
      bookName: "",
      description: "",
      authorName: "",
      isbnNumber: "",
      edition: 1,
      category: "",
      isAvailable: true,
      location: "",
      count: 1,
    });
    setIsAddingExistingBook(false); // Reset to adding new book
    setShowForm(true); // Open the modal
  };

  // UseEffect hook to load books on component mount
  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Book List</h2>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          {/* Add Book Button */}
          <button
            className="btn btn-primary mb-4"
            onClick={showAddBookForm} // Show Add Book Modal
          >
            Add New Book / Add Copies
          </button>

          {/* Book Table */}
          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2">Book Name</th>
                <th className="px-4 py-2">Description</th>
                <th className="px-4 py-2">Edition</th>
                <th className="px-4 py-2">Author</th>
                <th className="px-4 py-2">ISBN</th>
                <th className="px-4 py-2">Location</th>
                <th className="px-4 py-2">Category</th>
                <th className="px-4 py-2">Available Count</th>
                <th className="px-4 py-2">Loaned Count</th>
                <th className="px-4 py-2">Total Count</th>
                <th className="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.map((book) => (
                <tr key={book.isbnNumber}>
                  <td className="px-4 py-2">{book.bookName}</td>
                  <td className="px-4 py-2">{book.description}</td>
                  <td className="px-4 py-2">{book.edition}</td>
                  <td className="px-4 py-2">{book.authorName}</td>
                  <td className="px-4 py-2">{book.isbnNumber}</td>
                  <td className="px-4 py-2">{book.location}</td>
                  <td className="px-4 py-2">{book.category}</td>
                  <td className="px-4 py-2">{book.availableCount}</td>
                  <td className="px-4 py-2">{book.loanedCount}</td>
                  <td className="px-4 py-2">{book.totalCount}</td>
                  <td className="px-4 py-2">
                    {/* Edit Button */}
                    <button
                      className="btn btn-warning mr-2"
                      onClick={() => handleEdit(book.isbnNumber, book.location)}
                    >
                      Edit
                    </button>
                    {/* Delete Button */}
                    <button
                      className="bg-red-500 text-white px-4 py-2 rounded"
                      onClick={() => handleDelete(book.isbnNumber, book.location)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination */}
          <div className="flex justify-between items-center mt-4">
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 0}
            >
              Previous
            </button>
            <span className="text-lg">
              Page {currentPage + 1} of {totalPages}
            </span>
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages - 1}
            >
              Next
            </button>
          </div>

          {/* Modal for adding book or updating count */}
          {showForm && (
            <div className="modal-overlay">
              <div className="modal-window">
                <h3 className="modal-title">
                  {isAddingExistingBook ? "Add Copies to Existing Book" : "Add New Book"}
                </h3>
                <form onSubmit={handleAddBook} className="modal-form">
                <input
                    type="text"
                    name="isbnNumber"
                    value={formData.isbnNumber}
                    onChange={handleFormChange}
                    placeholder="ISBN Number"
                    required
                    disabled={isAddingExistingBook}
                  />
                  <button
                    type="button"
                    className="btn btn-secondary"
                    onClick={() => handleLookupBook(formData.isbnNumber)} // Trigger book lookup
                  >
                    Lookup Book
                  </button><br/>
                  <input
                    type="text"
                    name="bookName"
                    value={formData.bookName}
                    onChange={handleFormChange}
                    placeholder="Book Name"
                    required
                    disabled={isAddingExistingBook}
                  />
                  <input
                    type="text"
                    name="description"
                    value={formData.description}
                    onChange={handleFormChange}
                    placeholder="Description"
                    required
                    disabled={isAddingExistingBook}
                  />
                  <input
                    type="text"
                    name="authorName"
                    value={formData.authorName}
                    onChange={handleFormChange}
                    placeholder="Author Name"
                    required
                    disabled={isAddingExistingBook}
                  />
                  <input
                    type="number"
                    name="edition"
                    value={formData.edition}
                    onChange={handleFormChange}
                    placeholder="Edition"
                    required
                    disabled={isAddingExistingBook}
                  />
                  <input
                    type="text"
                    name="category"
                    value={formData.category}
                    onChange={handleFormChange}
                    placeholder="Category"
                    required
                    disabled={isAddingExistingBook}
                  />
                  <select
                    name="location"
                    value={formData.location}
                    onChange={handleFormChange}
                    required
                  >
                    <option value="">Select Location</option>
                    <option value="MIC Campus">MIC Campus</option>
                    <option value="UCM Campus">UCM Campus</option>
                  </select>
                  <input
                    type="number"
                    name="count"
                    value={formData.count}
                    onChange={handleFormChange}
                    placeholder="Count"
                    min="1"
                    required
                  />
                  <div className="modal-actions">
                    <button
                      type="button"
                      onClick={() => setShowForm(false)}
                    >
                      Cancel
                    </button>
                    <button type="submit">
                      {isAddingExistingBook ? "Add Copies" : "Add Book"}
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default BookList;
