import React, { useEffect, useState } from "react";
import axios from "axios";

const BooksList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);

  // Fetch books from the API
  const fetchBooks = async (page = 0) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8080/lms/books/grouped?page=${page}&size=10`
      );
      setBooks(response.data.books || []); // Safely handle missing books
      setTotalPages(response.data.totalPages || 0);
      setCurrentPage(page);
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      fetchBooks(newPage);
    }
  };

  // Handle book loan request
  const handleLoanRequest = async (isbn) => {
    try {
      await axios.post("http://localhost:8080/lms/book_loan_request_api", {
        isbn,
      });
      alert("Book successfully loaned!");
      fetchBooks(currentPage); // Refresh book list
    } catch (error) {
      console.error("Error processing loan request:", error);
      alert("Failed to loan the book. Try again later.");
    }
  };

  // Handle book reservation request
  const handleReservationRequest = async (isbn) => {
    try {
      await axios.post("http://localhost:8080/lms/book_loan_reserve_api", {
        isbn,
      });
      alert("Book successfully reserved!");
      fetchBooks(currentPage); // Refresh book list
    } catch (error) {
      console.error("Error processing reservation request:", error);
      alert("Failed to reserve the book. Try again later.");
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Available Books</h2>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2">Book Name</th>
                <th className="px-4 py-2">Description</th>
                <th className="px-4 py-2">Author</th>
                <th className="px-4 py-2">Edition</th>
                <th className="px-4 py-2">ISBN</th>
                <th className="px-4 py-2">Available Count</th>
                <th className="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.map((book) => (
                <tr key={book.isbnNumber}>
                  <td className="px-4 py-2">{book.bookName}</td>
                  <td className="px-4 py-2">{book.description}</td>
                  <td className="px-4 py-2">{book.authorName}</td>
                  <td className="px-4 py-2">{book.edition}</td>
                  <td className="px-4 py-2">{book.isbnNumber}</td>
                  <td className="px-4 py-2">{book.availableCount}</td>
                  <td className="px-4 py-2">
                    {book.availableCount > 0 ? (
                      <button
                        className="btn btn-primary mr-2"
                        onClick={() => handleLoanRequest(book.isbnNumber)}
                      >
                        Loan
                      </button>
                    ) : (
                      <button
                        className="btn btn-warning"
                        onClick={() => handleReservationRequest(book.isbnNumber)}
                      >
                        Reserve
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination */}
          <div className="flex justify-between items-center mt-4">
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 0}
            >
              Previous
            </button>
            <span className="text-lg">
              Page {currentPage + 1} of {totalPages}
            </span>
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages - 1}
            >
              Next
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default BooksList;
