import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import './AdminDashboard.css';

const BookList = () => {
  const [books, setBooks] = useState([]); // Store the list of books
  const [totalPages, setTotalPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [isExistingBook, setIsExistingBook] = useState(false); // Check if book is existing
  const [formData, setFormData] = useState({
    bookName: "",
    description: "",
    authorName: "",
    isbnNumber: "",
    edition: 1,
    category: "",
    isAvailable: true,
    location: "",
    count: 1, // New field for adding copies
  });
  const navigate = useNavigate(); // Use navigate for redirection

  // Fetch books from the API
  const fetchBooks = async (page = 0) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:8080/lms/books/grouped?page=${page}&size=10`);
      setBooks(response.data.books);
      setTotalPages(response.data.totalPages);
      setCurrentPage(page);
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      fetchBooks(newPage);
    }
  };

  // Handle form change
  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  // Handle adding a new book (or adding count to existing book)
  const handleAddBook = async (e) => {
    e.preventDefault();
    try {
      if (isExistingBook) {
        // Add copies to an existing book
        await axios.put(`http://localhost:8080/lms/books/${formData.isbnNumber}/addCopies`, { 
          count: formData.count,
          location: formData.location,
        });
      } else {
        // Create a new book
        for (let i = 0; i < formData.count; i++) {
          await axios.post("http://localhost:8080/lms/books", formData);
        }
      }
      fetchBooks(currentPage); // Reload the list of books
      setShowForm(false); // Close the modal after submission
    } catch (error) {
      console.error("Error adding book:", error);
    }
  };

  // Handle checking if book exists
  const handleISBNChange = async (e) => {
    const isbn = e.target.value;
    setFormData({ ...formData, isbnNumber: isbn });
    if (isbn) {
      try {
        const response = await axios.get(`http://localhost:8080/lms/books/${isbn}`);
        if (response.data) {
          setIsExistingBook(true);
          setFormData({
            ...formData,
            bookName: response.data.bookName,
            description: response.data.description,
            authorName: response.data.authorName,
            edition: response.data.edition,
            category: response.data.category,
            isAvailable: response.data.isAvailable,
            location: "", // User will enter location
            count: 1, // Default count
          });
        } else {
          setIsExistingBook(false);
        }
      } catch (error) {
        console.error("Error fetching book details:", error);
      }
    }
  };

  // Navigate to the EditBook page with ISBN and location as parameters
  const handleEdit = (isbn, location) => {
    navigate(`/editBook/${isbn}/${location}`);
  };

  // Navigate to the DeleteBook page with ISBN and location as parameters
  const handleDelete = (isbn, location) => {
    navigate(`/deleteBook/${isbn}/${location}`);
  };

  // Show the modal for adding a new book
  const showAddBookForm = () => {
    setFormData({
      bookName: "",
      description: "",
      authorName: "",
      isbnNumber: "",
      edition: 1,
      category: "",
      isAvailable: true,
      location: "",
      count: 1,
    });
    setIsExistingBook(false);
    setShowForm(true); // Open the modal
  };

  // UseEffect hook to load books on component mount
  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Book List</h2>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          {/* Add Book Button */}
          <button
            className="btn btn-primary mb-4"
            onClick={showAddBookForm} // Show Add Book Modal
          >
            Add New Book
          </button>

          {/* Book Table */}
          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2">Book Name</th>
                <th className="px-4 py-2">Description</th>
                <th className="px-4 py-2">Edition</th>
                <th className="px-4 py-2">Author</th>
                <th className="px-4 py-2">ISBN</th>
                <th className="px-4 py-2">Location</th>
                <th className="px-4 py-2">Category</th>
                <th className="px-4 py-2">Available Count</th>
                <th className="px-4 py-2">Loaned Count</th>
                <th className="px-4 py-2">Total Count</th>
                <th className="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.map((book) => (
                <tr key={book.isbnNumber}>
                  <td className="px-4 py-2">{book.bookName}</td>
                  <td className="px-4 py-2">{book.description}</td>
                  <td className="px-4 py-2">{book.edition}</td>
                  <td className="px-4 py-2">{book.authorName}</td>
                  <td className="px-4 py-2">{book.isbnNumber}</td>
                  <td className="px-4 py-2">{book.location}</td>
                  <td className="px-4 py-2">{book.category}</td>
                  <td className="px-4 py-2">{book.availableCount}</td>
                  <td className="px-4 py-2">{book.loanedCount}</td>
                  <td className="px-4 py-2">{book.totalCount}</td>
                  <td className="px-4 py-2">
                    {/* Edit Button */}
                    <button
                      className="btn btn-warning mr-2"
                      onClick={() => handleEdit(book.isbnNumber, book.location)}
                    >
                      Edit
                    </button>
                    {/* Delete Button */}
                    <button
                      className="bg-red-500 text-white px-4 py-2 rounded"
                      onClick={() => handleDelete(book.isbnNumber, book.location)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination */}
          <div className="flex justify-between items-center mt-4">
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 0}
            >
              Previous
            </button>
            <span className="text-lg">
              Page {currentPage + 1} of {totalPages}
            </span>
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages - 1}
            >
              Next
            </button>
          </div>
        </>
      )}

      {/* Add/Edit Book Modal */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal-window">
            <h3>{isExistingBook ? "Add Copies" : "Add New Book"}</h3>
            <form onSubmit={handleAddBook}>
              <input
                type="text"
                name="isbnNumber"
                value={formData.isbnNumber}
                onChange={handleISBNChange}
                placeholder="ISBN Number"
                required
                disabled={isExistingBook} // Disable for existing books
              />
              <input
                type="text"
                name="bookName"
                value={formData.bookName}
                onChange={handleFormChange}
                placeholder="Book Name"
                required
                disabled={isExistingBook} // Disable for existing books
              />
              <textarea
                name="description"
                value={formData.description}
                onChange={handleFormChange}
                placeholder="Description"
                required
                disabled={isExistingBook}
              />
              <input
                type="text"
                name="authorName"
                value={formData.authorName}
                onChange={handleFormChange}
                placeholder="Author"
                required
                disabled={isExistingBook}
              />
              <input
                type="number"
                name="edition"
                value={formData.edition}
                onChange={handleFormChange}
                placeholder="Edition"
                required
                disabled={isExistingBook}
              />
              <input
                type="text"
                name="category"
                value={formData.category}
                onChange={handleFormChange}
                placeholder="Category"
                required
                disabled={isExistingBook}
              />
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleFormChange}
                placeholder="Location"
                required
                disabled={!isExistingBook} // Enable only if adding copies to an existing book
              />
              <input
                type="number"
                name="count"
                value={formData.count}
                onChange={handleFormChange}
                placeholder="Number of Copies"
                required
              />
              <label>
                Available
                <input
                  type="checkbox"
                  name="isAvailable"
                  checked={formData.isAvailable}
                  onChange={handleFormChange}
                  disabled={isExistingBook} // Disable if adding copies
                />
              </label>
              <div className="mt-4">
                <button type="submit" className="btn btn-primary">
                  {isExistingBook ? "Add Copies" : "Add Book"}
                </button>
                <button
                  type="button"
                  className="btn btn-secondary ml-2"
                  onClick={() => setShowForm(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookList;
