import React from "react";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";

const DeleteBook = () => {
  const { isbn, location } = useParams();
  const navigate = useNavigate();

  // Handle delete
  const handleDelete = async () => {
    try {
        const response = await axios.get(`http://localhost:8080/lms/books/?isbn=${isbn}&location=${location}`);
      navigate('/'); // Redirect to the book list page after deleting
    } catch (error) {
      console.error("Error deleting book:", error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Are you sure you want to delete this book?</h2>
      <button
        onClick={handleDelete}
        className="bg-red-500 text-white px-4 py-2 rounded"
      >
        Delete
      </button>
      <button
        onClick={() => navigate('/')}
        className="ml-2 bg-gray-500 text-white px-4 py-2 rounded"
      >
        Cancel
      </button>
    </div>
  );
};

export default DeleteBook;
