import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const DeleteBooksPage = () => {
  const { isbn, location } = useParams();
  const [books, setBooks] = useState([]); // All book copies
  const navigate = useNavigate();

  // Fetch the books on page load
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/lms/books/list?isbn=${isbn}&location=${location}`);
        const booksData = Array.isArray(response.data) ? response.data : [];
        setBooks(booksData);
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };
    fetchBooks();
  }, [isbn, location]);

  // Handle book delete action
  const handleDelete = async (bookId) => {
    try {
      await axios.delete(`http://localhost:8080/lms/books/${bookId}`);
      alert("Book deleted successfully!");
      // Refresh the book list after deletion
      setBooks(books.filter(book => book.bookId !== bookId));
    } catch (error) {
      console.error("Error deleting book:", error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-6">Delete Books</h2>

      {/* Delete Book Table */}
      <table className="table-auto w-full border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="px-4 py-2">Book Id</th>
            <th className="px-4 py-2">Location</th>
            <th className="px-4 py-2">Available</th>
            <th className="px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.bookId}>
              <td className="px-4 py-2">{book.bookId}</td>
              <td className="px-4 py-2">{book.location}</td>
              <td className="px-4 py-2">{book.available ? "Yes" : "No"}</td>
              <td className="px-4 py-2">
                <button
                  className="btn btn-danger"
                  onClick={() => handleDelete(book.bookId)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DeleteBooksPage;
