import React, { useState } from 'react';
import { signUp } from '../../services/Authservice';
import './Auth.css'; // Importing existing auth CSS
import { useNavigate } from "react-router-dom";

const SignUpForm = () => {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        firstName: '',
        lastName: '',
        email: '',
        type: 'user',  // Default to 'user'
    });

    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate(); // Hook for navigation

    // Handle form changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Reset error and success messages before submitting
        setErrorMessage('');
        setSuccessMessage('');

        try {
            const response = await signUp(formData);  // Assuming signUp is an API call
            setLoading(false); // Hide loading spinner
            setSuccessMessage('Sign-up successful! Redirecting to login...');

            // Redirect to login page after success
            setTimeout(() => {
                navigate('/login'); // Redirect to login page after a delay
            }, 2000); // Wait for 2 seconds before redirecting

        } catch (error) {
            setLoading(false); // Hide loading spinner
            if (error.response) {
                // Handle backend errors (like duplicate username)
                setErrorMessage(error.response.data);  // Display the backend error message
            } else {
                // Handle other errors (network issues, etc.)
                setErrorMessage("An unexpected error occurred. Please try again.");
            }
        }
    };

    return (
        <div className="auth-container">
            <div className="auth-header">
                 Book<span>Nest</span>
            </div>
            <div className="auth-form">
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                    <h2 className="form-title">Sign Up</h2>

                        <label>Username:</label>
                        <input 
                            type="text" 
                            name="username" 
                            value={formData.username} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input 
                            type="password" 
                            name="password" 
                            value={formData.password} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label>First Name:</label>
                        <input 
                            type="text" 
                            name="firstName" 
                            value={formData.firstName} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label>Last Name:</label>
                        <input 
                            type="text" 
                            name="lastName" 
                            value={formData.lastName} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label>Email:</label>
                        <input 
                            type="email" 
                            name="email" 
                            value={formData.email} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label>User Type:</label>
                        <select name="type" value={formData.type} onChange={handleChange}>
                            <option value="user">User</option>
                            <option value="librarian">Librarian</option>
                            <option value="admin">Admin</option>
                        </select>
                    </div>
                    <button type="submit" className="submit-btn">Sign Up</button>
                </form>

                {errorMessage && <p className="error-message">{errorMessage}</p>}
                {successMessage && <p className="success-message">{successMessage}</p>}
            </div>
        </div>
    );
};

export default SignUpForm;
