import React, { useEffect, useState } from "react";
import axios from "axios";
import './AdminDashboard.css';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [totalPages, setTotalPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    bookName: "",
    description: "",
    authorName: "",
    isbnNumber: "",
    edition: 1,
    category: "",
    isAvailable: true,
    location: "",
  });
  const [editingBookId, setEditingBookId] = useState(null);
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [bookToDelete, setBookToDelete] = useState(null);
  const [bookListModal, setBookListModal] = useState(false);
  const [fetchedBooks, setFetchedBooks] = useState([]);
  const [isbn, setIsbn] = useState("");
  const [location, setLocation] = useState("");

  // Fetch books from the API
  const fetchBooks = async (page = 0) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:8080/lms/books/grouped?page=${page}&size=10`);
      setBooks(response.data.books);
      setTotalPages(response.data.totalPages);
      setCurrentPage(page);
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch books by ISBN and Location
  const fetchBooksByIsbnAndLocation = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8080/lms/books/search?isbn=${isbn}&location=${location}`
      );
      setFetchedBooks(response.data.books);
      setBookListModal(true);
    } catch (error) {
      console.error("Error fetching books by ISBN and Location:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      fetchBooks(newPage);
    }
  };

  // Handle form change
  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  // Handle adding a new book
  const handleAddBook = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8080/lms/books", formData);
      fetchBooks(currentPage);
      setShowForm(false); // Hide the form after submission
    } catch (error) {
      console.error("Error adding book:", error);
    }
  };

  // Handle editing a book
  const handleEditBook = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8080/lms/books/${editingBookId}`, formData);
      fetchBooks(currentPage);
      setShowForm(false); // Hide the form after editing
      setEditingBookId(null); // Reset the editing book ID
    } catch (error) {
      console.error("Error editing book:", error);
    }
  };

  // Handle deleting a book
  const handleDelete = async () => {
    if (bookToDelete) {
      try {
        await axios.delete(`http://localhost:8080/lms/books/${bookToDelete}`);
        fetchBooks(currentPage);
        setShowDeleteConfirmation(false); // Hide the confirmation modal
      } catch (error) {
        console.error("Error deleting book:", error);
      }
    }
  };

  // Show form for adding or editing a book
  const showFormForEdit = (book) => {
    setFormData({
      bookName: book.bookName,
      description: book.description,
      authorName: book.authorName,
      isbnNumber: book.isbnNumber,
      edition: book.edition,
      category: book.category,
      isAvailable: book.isAvailable,
      location: book.location,
    });
    setEditingBookId(book.isbnNumber); // Set the book ID to know which one we are editing
    setShowForm(true);
  };

  // UseEffect hook to load books on component mount
  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Book List</h2>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          <button
            className="btn btn-primary mb-4"
            onClick={() => {
              setShowForm(true);
              setEditingBookId(null); // Reset the editing state to add a new book
            }}
          >
            Add New Book
          </button>

          {/* Search Bar for ISBN and Location */}
          <div className="flex mb-4">
            <input
              type="text"
              placeholder="Search by ISBN"
              value={isbn}
              onChange={(e) => setIsbn(e.target.value)}
              className="input"
            />
            <input
              type="text"
              placeholder="Search by Location"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              className="input ml-2"
            />
            <button
              onClick={fetchBooksByIsbnAndLocation}
              className="btn btn-primary ml-2"
            >
              Search
            </button>
          </div>

          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2">Book Name</th>
                <th className="px-4 py-2">Description</th>
                <th className="px-4 py-2">Edition</th>
                <th className="px-4 py-2">Author</th>
                <th className="px-4 py-2">ISBN</th>
                <th className="px-4 py-2">Location</th>
                <th className="px-4 py-2">Category</th>
                <th className="px-4 py-2">Available Count</th>
                <th className="px-4 py-2">Loaned Count</th>
                <th className="px-4 py-2">Total Count</th>
                <th className="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.map((book) => (
                <tr key={book.isbnNumber}>
                  <td className="px-4 py-2">{book.bookName}</td>
                  <td className="px-4 py-2">{book.description}</td>
                  <td className="px-4 py-2">{book.edition}</td>
                  <td className="px-4 py-2">{book.authorName}</td>
                  <td className="px-4 py-2">{book.isbnNumber}</td>
                  <td className="px-4 py-2">{book.location}</td>
                  <td className="px-4 py-2">{book.category}</td>
                  <td className="px-4 py-2">{book.availableCount}</td>
                  <td className="px-4 py-2">{book.loanedCount}</td>
                  <td className="px-4 py-2">{book.totalCount}</td>

                  <td className="px-4 py-2">
                    <button
                      className="btn btn-warning mr-2"
                      onClick={() => showFormForEdit(book)}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => {
                        setBookToDelete(book.isbnNumber); // Set the book to delete
                        setShowDeleteConfirmation(true); // Show the delete confirmation modal
                      }}
                      className="bg-red-500 text-white px-4 py-2 rounded"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination */}
          <div className="flex justify-between items-center mt-4">
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 0}
            >
              Previous
            </button>
            <span className="text-lg">
              Page {currentPage + 1} of {totalPages}
            </span>
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages - 1}
            >
              Next
            </button>
          </div>
        </>
      )}

      {/* Add/Edit Book Modal */}
      {showForm && (
        <div className="modal-overlay">
          <div className="modal-window">
            <h3>{editingBookId ? "Edit Book" : "Add New Book"}</h3>
            <form onSubmit={editingBookId ? handleEditBook : handleAddBook}>
              <input
                type="text"
                name="bookName"
                value={formData.bookName}
                onChange={handleFormChange}
                placeholder="Book Name"
                required
              />
              <textarea
                name="description"
                value={formData.description}
                onChange={handleFormChange}
                placeholder="Description"
                required
              />
              <input
                type="text"
                name="authorName"
                value={formData.authorName}
                onChange={handleFormChange}
                placeholder="Author"
                required
              />
              <input
                type="text"
                name="isbnNumber"
                value={formData.isbnNumber}
                onChange={handleFormChange}
                placeholder="ISBN Number"
                required
              />
              <input
                type="number"
                name="edition"
                value={formData.edition}
                onChange={handleFormChange}
                placeholder="Edition"
                required
              />
              <input
                type="text"
                name="category"
                value={formData.category}
                onChange={handleFormChange}
                placeholder="Category"
                required
              />
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleFormChange}
                placeholder="Location"
                required
              />
              <label>
                Available
                <input
                  type="checkbox"
                  name="isAvailable"
                  checked={formData.isAvailable}
                  onChange={handleFormChange}
                />
              </label>
              <div className="mt-4">
                <button type="submit" className="btn btn-primary">
                  {editingBookId ? "Update Book" : "Add Book"}
                </button>
                <button
                  type="button"
                  className="btn btn-secondary ml-2"
                  onClick={() => setShowForm(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteConfirmation && (
        <div className="modal-overlay">
          <div className="modal-window">
            <h3>Are you sure you want to delete this book?</h3>
            <div>
              <button
                onClick={handleDelete}
                className="btn btn-danger"
              >
                Yes, Delete
              </button>
              <button
                onClick={() => setShowDeleteConfirmation(false)}
                className="btn btn-secondary ml-2"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Books by ISBN and Location Modal */}
      {bookListModal && (
        <div className="modal-overlay">
          <div className="modal-window">
            <h3>Books found for ISBN: {isbn} and Location: {location}</h3>
            <table className="table-auto w-full border-collapse border border-gray-300">
              <thead>
                <tr className="bg-gray-100">
                  <th className="px-4 py-2">Book Name</th>
                  <th className="px-4 py-2">Description</th>
                  <th className="px-4 py-2">Edition</th>
                  <th className="px-4 py-2">Author</th>
                  <th className="px-4 py-2">ISBN</th>
                  <th className="px-4 py-2">Location</th>
                  <th className="px-4 py-2">Category</th>
                  <th className="px-4 py-2">Available Count</th>
                  <th className="px-4 py-2">Loaned Count</th>
                  <th className="px-4 py-2">Total Count</th>
                </tr>
              </thead>
              <tbody>
                {fetchedBooks.map((book) => (
                  <tr key={book.isbnNumber}>
                    <td className="px-4 py-2">{book.bookName}</td>
                    <td className="px-4 py-2">{book.description}</td>
                    <td className="px-4 py-2">{book.edition}</td>
                    <td className="px-4 py-2">{book.authorName}</td>
                    <td className="px-4 py-2">{book.isbnNumber}</td>
                    <td className="px-4 py-2">{book.location}</td>
                    <td className="px-4 py-2">{book.category}</td>
                    <td className="px-4 py-2">{book.availableCount}</td>
                    <td className="px-4 py-2">{book.loanedCount}</td>
                    <td className="px-4 py-2">{book.totalCount}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="mt-4">
              <button
                onClick={() => setBookListModal(false)}
                className="btn btn-secondary"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookList;
