import React, { useState } from 'react';
import { FaUser, FaBook, FaClipboardList, FaSignOutAlt, FaBars } from 'react-icons/fa';
import './LibrarianDashboard.css';

const LibrarianDashboard = () => {
    const [isCollapsed, setIsCollapsed] = useState(false);
    const username = localStorage.getItem('username') || 'Librarian';

    const toggleSidebar = () => {
        setIsCollapsed((prevState) => !prevState);
    };

    return (
        <div className="librarian-dashboard">
            {/* Sidebar */}
            <div className={`librarian-sidebar ${isCollapsed ? 'collapsed' : ''}`}>
                {/* Welcome Section */}
                <div className={`librarian-welcome ${isCollapsed ? 'collapsed' : ''}`}>
                    <FaUser size={20} />
                    <span>Welcome, {username}</span>
                </div>

                {/* Sidebar Menu */}
                <ul className="librarian-menu">
                    <li className="librarian-menu-item">
                        <FaClipboardList className="librarian-menu-item-icon" />
                        <span className="librarian-menu-item-text">Manage Loan Requests</span>
                    </li>
                    <li className="librarian-menu-item">
                        <FaBook className="librarian-menu-item-icon" />
                        <span className="librarian-menu-item-text">Track Resources</span>
                    </li>
                    <li className="librarian-menu-item">
                        <FaSignOutAlt className="librarian-menu-item-icon" />
                        <span className="librarian-menu-item-text">Logout</span>
                    </li>
                </ul>

                {/* Toggle Button */}
                <button className="librarian-toggle-button" onClick={toggleSidebar}>
                    <FaBars />
                </button>
            </div>

            {/* Main Content */}
            <div className={`librarian-main-content ${isCollapsed ? 'collapsed' : ''}`}>
                <h1>Welcome to the Librarian Dashboard</h1>
                {/* Additional content goes here */}
            </div>
        </div>
    );
};

export default LibrarianDashboard;
