import React, { useState } from 'react';
import { FaUser, FaBook, FaClipboardList, FaSignOutAlt, FaBars } from 'react-icons/fa';
import { useNavigate } from 'react-router-dom';

const LibrarianDashboard = () => {
    const [isCollapsed, setIsCollapsed] = useState(false);

    const toggleSidebar = () => {
        setIsCollapsed(!isCollapsed);
    };

    const navigate = useNavigate();
    const username = localStorage.getItem('username'); 

    const handleLogout = () => {
        console.log('Logging out...');
        navigate('/login', { replace: true });
    };

    const navigateToManageLoanRequests = () => {
        navigate('/manage-loan-requests'); 
    };
    const navigateToBookList = () => {
        navigate('/books'); 
    };
    return (
        <div
            className={`h-screen bg-gray-800 text-white ${
                isCollapsed ? 'w-16' : 'w-52'
            } transition-all duration-300`}
        >
            <div className="flex items-center justify-between p-4">
                <div className="flex items-center">
                    <FaBars className="cursor-pointer" onClick={toggleSidebar} />
                </div>
            </div>

            <div className="mt-10">
                <div className="flex items-center p-4 hover:bg-gray-700 cursor-pointer">
                    <FaUser size={20} />
                    {!isCollapsed && <span className="ml-4">Welcome User</span>}
                </div>

                <div className="flex items-center p-4 hover:bg-gray-700 cursor-pointer">
                    <FaBook size={20} />
                    {!isCollapsed && <span className="ml-4">Books</span>}
                </div>

                <div className="flex items-center p-4 hover:bg-gray-700 cursor-pointer">
                    <FaClipboardList size={20} />
                    {!isCollapsed && <span className="ml-4">Requests</span>}
                </div>

                <div className="flex items-center p-4 hover:bg-gray-700 cursor-pointer">
                    <FaSignOutAlt size={20} />
                    {!isCollapsed && <span className="ml-4">Logout</span>}
                </div>
            </div>
        </div>
    );
};

export default LibrarianDashboard;
