import React from 'react';
import './AdminDashboard.css';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
    const navigate = useNavigate();
    const username = localStorage.getItem('username'); 

    const handleLogout = () => {
        console.log('Logging out...');
        navigate('/login', { replace: true });
    };
    const navigateToBookList = () => {
      navigate('/books'); 
  };

    return (
          <div className="librarian-main-content">
          <div className="librarian-header">
              <h1>Welcome, {username || 'Librarian'}</h1>
              <button className="librarian-logout-button" onClick={handleLogout}>Logout</button>
          </div>

          <div className="librarian-section">
              <h2>Inventory Management</h2>
              <button className="librarian-button" onClick={navigateToBookList}>Manage Book Resources</button>
          </div>
      </div>
    );
};

export default AdminDashboard;
