import React from 'react';
import { Pie, Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from 'chart.js';

// Register required Chart.js components
ChartJS.register(ArcElement, CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend);

// Sample data for Pie chart (Book Availability)
const pieData = {
  labels: ['Available Books', 'Loaned Books', 'Reserved Books'],
  datasets: [
    {
      data: [300, 50, 100],
      backgroundColor: ['#1E40AF', '#DC2626', '#FACC15'],
      hoverBackgroundColor: ['#1E3A8A', '#991B1B', '#EAB308'],
    },
  ],
};

// Sample data for Line chart (Weekly Activity)
const lineData = {
  labels: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'],
  datasets: [
    {
      label: 'Books Loaned',
      data: [5, 10, 15, 20, 25, 30, 35],
      borderColor: '#1E40AF',
      backgroundColor: 'rgba(30, 64, 175, 0.2)',
      fill: true,
    },
    {
      label: 'Books Reserved',
      data: [3, 5, 8, 13, 18, 25, 30],
      borderColor: '#DC2626',
      backgroundColor: 'rgba(220, 38, 38, 0.2)',
      fill: true,
    },
  ],
};

const AdminInsights = () => {
  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Dashboard Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-100">Admin Insights Dashboard</h1>
          <p className="text-gray-400">Key metrics and insights for library management</p>
        </div>

        {/* Cards Section */}
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-6 mb-8">
          <div className="bg-gray-800 shadow-md rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-gray-100">New Users</h2>
            <p className="text-4xl font-bold text-blue-400">25</p>
          </div>
          <div className="bg-gray-800 shadow-md rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-gray-100">Total Users</h2>
            <p className="text-4xl font-bold text-green-400">1,250</p>
          </div>
          <div className="bg-gray-800 shadow-md rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-gray-100">Total Librarians</h2>
            <p className="text-4xl font-bold text-yellow-400">10</p>
          </div>
          <div className="bg-gray-800 shadow-md rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-gray-100">Pending Book Requests</h2>
            <p className="text-4xl font-bold text-red-400">15</p>
          </div>
        </div>

        {/* Charts Section */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Pie Chart */}
          <div className="bg-gray-800 shadow-md rounded-lg p-6">
            <h3 className="text-xl font-semibold text-gray-100 mb-4">Books Status Distribution</h3>
            <div className="w-48 h-48">
                <Pie data={pieData} />
              </div>          </div>

          {/* Line Chart */}
          <div className="bg-gray-800 shadow-md rounded-lg p-6">
            <h3 className="text-xl font-semibold text-gray-100 mb-4">Weekly Activity</h3>
            <Line data={lineData} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminInsights;
