import React from 'react';
import './AdminDashboard.css';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
    const navigate = useNavigate();
    const username = localStorage.getItem('username'); 

    const handleLogout = () => {
        console.log('Logging out...');
        navigate('/login', { replace: true });
    };

    return (
        <div className="main-content">
            <div className="dashboard-header">
            <h1>Welcome, {username || 'Admin'}</h1>
            <button className="logout-button" onClick={handleLogout}>Logout</button>
            </div>

            <div className="section">
                <h2>System Management</h2>
                <p>Manage system settings and user accounts.</p>
                <button className="button">View Users</button>
            </div>

            <div className="section">
                <h2>Resource Management</h2>
                <p>Manage resources (books, DVDs, CDs).</p>
                <button className="button">Add New Resource</button>
                <button className="button">View Resources</button>
            </div>
        </div>
    );
};

export default AdminDashboard;
