import React, { useEffect, useState } from "react";
import axios from "axios";
import './UserDashboard.css'; // Assuming you have the required CSS for modal

// Modal component for displaying book details and handling request/reservation
const BookModal = ({ isOpen, onClose, book, onSubmit, formType, formData, setFormData }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData); // Submitting the formData to parent
  };

  // Reset form data when modal is closed
  const handleClose = () => {
    setFormData({
      firstName: "",
      lastName: "",
      studentId: "",
      reservationDate: "",
    });
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-window">
        <h2 className="text-2xl font-semibold mb-4">{formType} Book</h2>
        <p><strong>Book Name:</strong> {book.bookName}</p>
        <p><strong>Description:</strong> {book.description}</p>
        <p><strong>Author:</strong> {book.authorName}</p>
        <p><strong>ISBN:</strong> {book.isbnNumber}</p>
        <p><strong>Edition:</strong> {book.edition}</p>
        <p><strong>Category:</strong> {book.category}</p>

        <form onSubmit={handleSubmit} className="modal-form">
          <label>
            First Name:
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              className="block w-full mb-2 p-2 border"
              required
            />
          </label>
          <label>
            Last Name:
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              className="block w-full mb-2 p-2 border"
              required
            />
          </label>
          <label>
            Student ID (700#):
            <input
              type="text"
              name="studentId"
              value={formData.studentId}
              onChange={handleChange}
              className="block w-full mb-2 p-2 border"
              required
            />
          </label>
          {formType === "Reserve" && (
            <label>
              Reservation Date:
              <input
                type="date"
                name="reservationDate"
                value={formData.reservationDate}
                onChange={handleChange}
                min={new Date().toISOString().split("T")[0]}
                max={new Date(new Date().setDate(new Date().getDate() + 30)).toISOString().split("T")[0]}
                className="block w-full mb-2 p-2 border"
                required
              />
            </label>
          )}
          <button type="submit" className="mt-4 bg-blue-500 text-white py-2 px-4 rounded">
            {formType}
          </button>
        </form>
        <button onClick={handleClose} className="mt-4 text-gray-500">Close</button>
      </div>
    </div>
  );
};

const UserBookList = () => {
  const [books, setBooks] = useState([]);
  const [locations, setLocations] = useState([]);
  const [userId, setUserId] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState("MIC Campus");
  const [loading, setLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [bookForModal, setBookForModal] = useState(null);
  const [formType, setFormType] = useState("");
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    studentId: "",
    reservationDate: "",
  });
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    fetchLocations();
    fetchUserId();
  }, []);

  const fetchLocations = async () => {
    try {
      const response = await axios.get("http://localhost:8080/lms/books/locations");
      setLocations(response.data);
      fetchBooks("MIC Campus", currentPage); // Default to MIC Campus on load
    } catch (error) {
      console.error("Error fetching locations:", error);
    }
  };

  const fetchBooks = async (location, page) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8080/lms/books/grouped?page=${page}&size=10&location=${location}`
      );
      setBooks(response.data.books);
      setTotalPages(response.data.totalPages); // Set total pages from response
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  const fetchUserId = async () => {
    const username = localStorage.getItem('username');
    const type = localStorage.getItem('role');

    // API call to fetch userId based on username
    try {
        const response = await fetch(`http://localhost:8080/lms/auth/getUserId?username=${username}&type=${type}`);
        const data = await response.json();
        if (data.userId) {
            setUserId(data.userId);
        } else {
          alert('User ID not found');
        }
    } catch (err) {
      alert('Error fetching userId');
    }
  };

  const handleLocationChange = (e) => {
    const selectedLocation = e.target.value;
    setSelectedLocation(selectedLocation);
    fetchBooks(selectedLocation, currentPage); // Fetch books for selected location
  };

  const openModal = (book, type) => {
    setBookForModal(book);
    setFormType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setBookForModal(null);
    setFormData({
      firstName: "",
      lastName: "",
      studentId: "",
      reservationDate: "",
    });
  };

  const handleCheckout = async (formData) => {
    try {
      // First, fetch the list of books and check for an available book
      const response = await axios.get(`http://localhost:8080/lms/books/list?isbn=${bookForModal.isbnNumber}&location=${selectedLocation}`);
      const availableBook = response.data.find(book => book.available); 

      if (availableBook) {
        const checkoutResponse = await axios.post('http://localhost:8080/lms/loan-reservation/requestCheckout', null,{
          params: {
            bookId: availableBook.bookId,
            userId: userId, 
          },}
        );
          if (checkoutResponse.status === 200) {
            alert('Checkout Successful');
          } else {
            alert('Something went wrong during checkout');
          }
      } else {
        alert('No available books for checkout');
      }
    } catch (error) {
      console.error("Checkout API error:", error);
      alert('Error during checkout. Please try again later.');
    } finally {
      closeModal();
    }
  };

  const handleReservation = async (formData) => {
    try {
      const reservationDate = new Date(formData.reservationDate).toISOString(); 

      const response = await axios.post('http://localhost:8080/lms/loan-reservation/requestReservation', {
        isbn: bookForModal.isbnNumber,
        userId: userId,
        reservationDate: reservationDate, // Send ISO 8601 string
      });

      if (response.status === 200) {
        alert('Reservation Successful');
      } else {
        alert('Something went wrong during reservation');
      }
    } catch (error) {
      console.error("Reservation API error:", error);
      alert('Error during reservation. Please try again later.');
    } finally {
      closeModal();
    }
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
    fetchBooks(selectedLocation, page); // Fetch books for the selected location and page
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Book List</h2>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          <div className="mb-4">
            <label htmlFor="location" className="mr-2">
              Select Location:
            </label>
            <select
              id="location"
              value={selectedLocation}
              onChange={handleLocationChange}
              className="p-2 border rounded"
            >
              {locations.map((location) => (
                <option key={location} value={location}>
                  {location}
                </option>
              ))}
            </select>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border border-gray-200">
              <thead>
                <tr>
                  <th className="py-2 px-4 border">Book Name</th>
                  <th className="py-2 px-4 border">Description</th>
                  <th className="py-2 px-4 border">Author</th>
                  <th className="py-2 px-4 border">Category</th>
                  <th className="py-2 px-4 border">Action</th>
                </tr>
              </thead>
              <tbody>
                {books.map((book) => (
                  <tr key={book.isbnNumber}>
                    <td className="py-2 px-4 border">{book.bookName}</td>
                    <td className="py-2 px-4 border">{book.description}</td>
                    <td className="py-2 px-4 border">{book.authorName}</td>
                    <td className="py-2 px-4 border">{book.category}</td>
                    <td className="py-2 px-4 border">
                      {book.available ? (
                        <button
                          className="bg-blue-500 text-white py-2 px-4 rounded"
                          onClick={() => openModal(book, "Checkout")}
                        >
                          Checkout
                        </button>
                      ) : (
                        <button
                          className="bg-green-500 text-white py-2 px-4 rounded"
                          onClick={() => openModal(book, "Reserve")}
                        >
                          Reserve
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="flex justify-center mt-4">
            <button
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 0}
              className="bg-gray-300 text-gray-700 py-2 px-4 rounded mr-2"
            >
              Prev
            </button>
            <span className="py-2 px-4">{`Page ${currentPage + 1} of ${totalPages}`}</span>
            <button
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage + 1 === totalPages}
              className="bg-gray-300 text-gray-700 py-2 px-4 rounded ml-2"
            >
              Next
            </button>
          </div>
        </>
      )}

      <BookModal
        isOpen={isModalOpen}
        onClose={closeModal}
        book={bookForModal}
        onSubmit={formType === "Checkout" ? handleCheckout : handleReservation}
        formType={formType}
        formData={formData}
        setFormData={setFormData}
      />
    </div>
  );
};

export default UserBookList;
