import React, { useState } from "react";
import { login } from '../../services/Authservice';
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom"; 
import "./Auth.css";

const Login = () => {
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await login(formData); // Await the resolved promise
      const { username, role } = response.data; // Destructure response for username and role
      // Store username in localStorage
      localStorage.setItem('username', username);
      localStorage.setItem('role', role);
      console.log(`Logged in as: ${username}, Role: ${role}`);

      // Redirect based on role
      if (role.toLowerCase() === "admin") navigate("/admin");
      else if (role.toLowerCase() === "librarian") navigate("/librarian");
      else if (role.toLowerCase() === "user") navigate("/user");
      else setError("Unexpected role. Please contact support.");
    } catch (err) {
      setError("Invalid username or password."); // Display error if login fails
    }
  };

  return (
    <div className="auth-container">
    <div className="auth-header">
          Book<span>Nest</span>
      </div>

      <form onSubmit={handleSubmit}>
        <h2>Login</h2>
        {error && <p className="error">{error}</p>}
        <div>
          <label>Username:</label>
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Login</button>
         {/* Add Sign Up link */}
      <p>
        Don't have an account? <Link to="/signup">Sign up here</Link>
      </p>
      </form>


    </div>
  );
};

export default Login;
