import React, { useEffect, useState } from "react";
import axios from "axios";

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [loading, setLoading] = useState(true);

  // Fetch books grouped by category
  const fetchBooks = async (page = 0) => {
    try {
      setLoading(true);
      const response = await axios.get(
        `http://localhost:8080/lms/books/grouped?page=${page}&size=10`
      );

      setBooks(response.data); // Assuming `content` contains grouped books
      setTotalPages(response.data.totalPages);
      setCurrentPage(response.data.pageable.pageNumber);
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      fetchBooks(newPage);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Book List</h2>

      {/* Book Groups */}
      {books.map((group) => (
        <div key={group.category} className="mb-6">
          <h3 className="text-xl font-medium mb-2">{group.category}</h3>
          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2">Book Name</th>
                <th className="px-4 py-2">Author</th>
                <th className="px-4 py-2">Edition</th>
                <th className="px-4 py-2">Available Copies</th>
              </tr>
            </thead>
            <tbody>
              {group.books.map((book) => (
                <tr key={book.isbnNumber}>
                  <td className="px-4 py-2">{book.bookName}</td>
                  <td className="px-4 py-2">{book.authorName}</td>
                  <td className="px-4 py-2">{book.edition}</td>
                  <td className="px-4 py-2">{book.availableCopies}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}

      {/* Pagination */}
      <div className="flex justify-between mt-4">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 0}
          className="btn btn-secondary"
        >
          Previous
        </button>
        <span>
          Page {currentPage + 1} of {totalPages}
        </span>
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage + 1 >= totalPages}
          className="btn btn-secondary"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default BookList;
