import React, { useState, useEffect } from 'react';
import axios from 'axios';

const IssueBooks = () => {
  const [loanRequests, setLoanRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBook, setSelectedBook] = useState(null);
  const [userId, setUserId] = useState(null); // State for userId

  useEffect(() => {
    // Fetch all loan requests with pending status
    const fetchLoanRequests = async () => {
      try {
        const response = await axios.get('http://localhost:8080/lms/loan-reservation/pending-loan-requests');
        setLoanRequests(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch loan requests. Please try again later.');
        setLoading(false);
      }
    };

    // Fetch userId from localStorage or API
    const fetchUserId = async () => {
      const username = localStorage.getItem('username');
      const type = localStorage.getItem('role');

      try {
        const response = await fetch(`http://localhost:8080/lms/auth/getUserId?username=${username}&type=${type}`);
        const data = await response.json();
        if (data.userId) {
          setUserId(data.userId);
        } else {
          alert('User ID not found');
        }
      } catch (err) {
        alert('Error fetching userId');
      }
    };

    fetchLoanRequests();
    fetchUserId();
  }, []);

  const handleApprove = async (loanId) => {
    try {
      const response = await axios.put(
        `http://localhost:8080/lms/loan-reservation/updateCheckout/${loanId}`,
        null,
        {
          params: {
            status: 'Ready for Pickup', // Set the status to Ready for Pickup
            librarianId: userId,
          },
        }
      );
      if (response.status === 200) {
        alert('Loan request approved and marked for pickup!');
        setLoanRequests((prev) =>
          prev.filter((request) => request.loanId !== loanId)
        );
      } else {
        alert('Failed to approve the loan request.');
      }
    } catch (error) {
      console.error('Error approving loan request:', error);
      alert('Error approving loan request. Please try again later.');
    }
  };

  const handleCancel = async (loanId) => {
    try {
      const response = await axios.put(
        `http://localhost:8080/lms/loan-reservation/updateCheckout/${loanId}`,
        null,
        {
          params: {
            status: 'Cancelled',
            librarianId: userId,
          },
        }
      );
      if (response.status === 200) {
        alert('Loan request canceled.');
        setLoanRequests((prev) =>
          prev.filter((request) => request.loanId !== loanId)
        );
      } else {
        alert('Failed to cancel the loan request.');
      }
    } catch (error) {
      console.error('Error canceling loan request:', error);
      alert('Error canceling loan request. Please try again later.');
    }
  };

  const handleBookClick = (book) => {
    setSelectedBook(book);
  };

  const handleCloseModal = () => {
    setSelectedBook(null);
  };

  const handlePickupReminder = (loanId, status) => {
    const pickupTimeLimit = 24 * 60 * 60 * 1000; // 24 hours in milliseconds
    const now = new Date().getTime();
    const readyForPickupTime = new Date().getTime(); // Replace this with the actual time the status changed

    if (status === 'Ready for Pickup' && now - readyForPickupTime > pickupTimeLimit) {
      handleCancel(loanId);
    }
  };

  if (loading) {
    return <div>Loading loan requests...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Issue Books - Loan Requests</h1>
      {loanRequests.length === 0 ? (
        <div className="text-center">No pending loan requests.</div>
      ) : (
        <table className="table-auto w-full border border-gray-300">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-4 py-2">Book Name</th>
              <th className="border px-4 py-2">Author</th>
              <th className="border px-4 py-2">User Name</th>
              <th className="border px-4 py-2">Requested Date</th>
              <th className="border px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {loanRequests.map((request) => (
              <tr key={request.loanId}>
                <td className="border px-4 py-2">
                  <a
                    href="#"
                    onClick={() => handleBookClick(request)}
                    className="text-blue-500 hover:underline"
                  >
                    {request.bookName}
                  </a>
                </td>
                <td className="border px-4 py-2">{request.authorName}</td>
                <td className="border px-4 py-2">{request.userName}</td>
                <td className="border px-4 py-2">
                  {new Date(request.requestedDate).toLocaleString()}
                </td>
                <td className="border px-4 py-2 text-center">
                  <button
                    onClick={() => handleApprove(request.loanId)}
                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-2 rounded mr-2"
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => handleCancel(request.loanId)}
                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded"
                  >
                    Cancel
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Modal to display complete book details */}
      {selectedBook && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-6 rounded-lg w-1/3">
            <h2 className="text-xl font-bold mb-4">{selectedBook.bookName}</h2>
            <p><strong>Author:</strong> {selectedBook.authorName}</p>
            <p><strong>Category:</strong> {selectedBook.category}</p>
            <p><strong>ISBN:</strong> {selectedBook.isbnNumber}</p>
            <p><strong>Edition:</strong> {selectedBook.edition}</p>
            <p><strong>Description:</strong> {selectedBook.description}</p>
            <p><strong>Location:</strong> {selectedBook.location || 'Not specified'}</p>
            <button
              onClick={handleCloseModal}
              className="mt-4 bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-4 rounded"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default IssueBooks;
