import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookingList = () => {
  const [bookingList, setBookingList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userId, setUserId] = useState(null); // State for userId

  useEffect(() => {
    const fetchUserId = async () => {
      const username = localStorage.getItem('username');
      const type = localStorage.getItem('role');

      // API call to fetch userId based on username
      try {
        const response = await fetch(`http://localhost:8080/lms/auth/getUserId?username=${username}&type=${type}`);
        const data = await response.json();
        if (data.userId) {
          setUserId(data.userId);
        } else {
          alert('User ID not found');
        }
      } catch (err) {
        alert('Error fetching userId');
      }
    };

    fetchUserId();
  }, []);

  useEffect(() => {
    if (userId) {
      const fetchBookingList = async () => {
        try {
          const response = await axios.get(`http://localhost:8080/lms/loan-reservation/loan-requests/user/${userId}`);
          setBookingList(response.data);
          setLoading(false);
        } catch (err) {
          setError('Failed to fetch booking list. Please try again later.');
          setLoading(false);
        }
      };

      fetchBookingList();
    }
  }, [userId]);

  const handleRenew = async (loanId, bookId) => {
    try {
        const response = await axios.post('http://localhost:8080/lms/loan-reservation/requestRenewal', null,{
            params: {
              loanId: loanId,
              userId: userId, 
            },}
          );      
    if (response.status === 200) {
        alert('Book renewed successfully!');
        // Update the list after renewal
        setBookingList((prev) => prev.map((booking) => 
          booking.loanId === loanId ? { ...booking, renewalCount: booking.renewalCount + 1 } : booking
        ));
      } else {
        alert('Failed to renew the book.');
      }
    } catch (error) {
      console.error('Error renewing book:', error);
      alert('Error renewing the book. Please try again later.');
    }
  };

  const handleCancelReservation = async (loanId) => {
    try {
      const response = await axios.post(`http://localhost:8080/lms/loan-reservation/cancel-reservation/${loanId}`);
      if (response.status === 200) {
        alert('Reservation canceled successfully!');
        // Update the list after cancellation
        setBookingList((prev) => prev.filter((booking) => booking.loanId !== loanId));
      } else {
        alert('Failed to cancel reservation.');
      }
    } catch (error) {
      console.error('Error canceling reservation:', error);
      alert('Error canceling reservation. Please try again later.');
    }
  };

  if (loading) {
    return <div>Loading booking list...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Your Booking List</h1>
      {bookingList.length === 0 ? (
        <div className="text-center">No bookings found.</div>
      ) : (
        <table className="table-auto w-full border border-gray-300">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-4 py-2">Book Name</th>
              <th className="border px-4 py-2">Author</th>
              <th className="border px-4 py-2">Status</th>
              <th className="border px-4 py-2">Due Date</th>
              <th className="border px-4 py-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {bookingList.map((booking) => (
              <tr key={booking.loanId}>
                <td className="border px-4 py-2">{booking.bookName}</td>
                <td className="border px-4 py-2">{booking.authorName}</td>
                <td className="border px-4 py-2">
                  {booking.status}
                </td>
                <td className="border px-4 py-2">
                  {booking.dueDate ? new Date(booking.dueDate).toLocaleDateString() : '-'}
                </td>
                <td className="border px-4 py-2 text-center">
                  {booking.status === 'Loaned' && booking.renewalCount < 3 && (
                    <button
                      onClick={() => handleRenew(booking.loanId, booking.bookId)}
                      className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 rounded mr-2"
                    >
                      Renew
                    </button>
                  )}
                  {booking.status === 'Pending Reservation' && (
                    <button
                      onClick={() => handleCancelReservation(booking.loanId)}
                      className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded"
                    >
                      Cancel Reservation
                    </button>
                  )}
                    {booking.status === 'Pending Renewal' && (
                    <button
                      onClick={() => handleCancelRenewal(booking.loanId)}
                      className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 rounded"
                    >
                      Cancel Reservation
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default BookingList;
