import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './LibrarianDashboard.css';

const LibrarianDashboard = ({ children }) => {
    const navigate = useNavigate();
    const username = localStorage.getItem('username') || 'Librarian';
    const [sidebarOpen, setSidebarOpen] = useState(true);

    const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

    const handleLogout = () => {
        localStorage.clear();
        navigate('/login', { replace: true });
    };

    return (
        <div className="librarian-dashboard">
            {/* Sidebar */}
            <div className={`librarian-sidebar ${sidebarOpen ? 'open' : 'collapsed'}`}>
                <button className="toggle-sidebar" onClick={toggleSidebar}>
                    {sidebarOpen ? '←' : '→'}
                </button>
                <Link to="/manage-loan-requests">Manage Loan Requests</Link>
                <Link to="/books">Book Inventory</Link>
            </div>

            {/* Main Content */}
            <div className="librarian-main-content">
                {/* Header */}
                <div className="librarian-header">
                    <h1>Welcome, {username}</h1>
                    <button className="librarian-logout-button" onClick={handleLogout}>
                        Logout
                    </button>
                </div>

                {/* Dynamic Content */}
                <div className="content">{children}</div>
            </div>
        </div>
    );
};

export default LibrarianDashboard;
