import React, { useEffect, useState } from "react";
import axios from "axios";
import { Line, Pie } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";

// Registering the chart components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const AdminInsights = () => {
  const [stats, setStats] = useState({
    totalBooks: 0,
    totalLoans: 0,
    totalReservations: 0,
    booksByCategory: {},
    loansByMonth: [],
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const booksData = await axios.get("http://your-api.com/books");
        const loansData = await axios.get("http://your-api.com/loans");
        const reservationsData = await axios.get("http://your-api.com/reservations");

        setStats({
          totalBooks: booksData.data.length,
          totalLoans: loansData.data.length,
          totalReservations: reservationsData.data.length,
          booksByCategory: {
            Books: booksData.data.filter(book => book.category === "Books").length,
            Ebooks: booksData.data.filter(book => book.category === "E-books").length,
            AudioBooks: booksData.data.filter(book => book.category === "Audio Books").length,
          },
          loansByMonth: [/* Loan count data for each month, e.g., 10, 15, 8, 20 */],
        });
      } catch (error) {
        console.error("Error fetching data", error);
      }
    };

    fetchData();
  }, []);

  // Pie chart data for Book Categories
  const categoryData = {
    labels: ["Books", "E-books", "Audio Books"],
    datasets: [
      {
        data: [
          stats.booksByCategory.Books,
          stats.booksByCategory.Ebooks,
          stats.booksByCategory.AudioBooks,
        ],
        backgroundColor: ["#4CAF50", "#FF9800", "#2196F3"],
      },
    ],
  };

  // Line chart data for Loan Trends
  const loanData = {
    labels: ["January", "February", "March", "April", "May"], // Example months, update as needed
    datasets: [
      {
        label: "Books Loaned",
        data: stats.loansByMonth,
        fill: false,
        borderColor: "#4CAF50",
        tension: 0.1,
      },
    ],
  };

  return (
    <div className="container mx-auto px-4 py-10">
      {/* Stats Section */}
      <div className="grid grid-cols-3 gap-6">
        <div className="bg-white shadow-lg rounded-lg p-6">
          <h2 className="text-2xl font-semibold text-gray-800">Total Books</h2>
          <p className="text-xl text-gray-500">{stats.totalBooks}</p>
        </div>
        <div className="bg-white shadow-lg rounded-lg p-6">
          <h2 className="text-2xl font-semibold text-gray-800">Total Active Loans</h2>
          <p className="text-xl text-gray-500">{stats.totalLoans}</p>
        </div>
        <div className="bg-white shadow-lg rounded-lg p-6">
          <h2 className="text-2xl font-semibold text-gray-800">Total Reservations</h2>
          <p className="text-xl text-gray-500">{stats.totalReservations}</p>
        </div>
      </div>

      {/* Book Category Distribution Chart */}
      <div className="mt-8">
        <h2 className="text-xl font-semibold text-gray-800">Book Category Distribution</h2>
        <Pie data={categoryData} />
      </div>

      {/* Loaned Books Insights */}
      <div className="mt-8">
        <h2 className="text-xl font-semibold text-gray-800">Loan Trends</h2>
        <Line data={loanData} />
      </div>
    </div>
  );
};

export default AdminInsights;
