import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const EditBook = () => {
  const [books, setBooks] = useState([]); // State to store the list of books
  const [formData, setFormData] = useState({
    bookId: "",
    bookName: "",
    description: "",
    authorName: "",
    isbnNumber: "",
    edition: 1,
    category: "",
    isAvailable: true,
    location: "",
  });
  const [showModal, setShowModal] = useState(false); // State to handle modal visibility
  const navigate = useNavigate();

  // Fetch the list of books
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get("http://localhost:8080/lms/books/grouped");
        const booksData = Array.isArray(response.data) ? response.data : [];
        setBooks(booksData); // Safely set books state to an array
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };
    fetchBooks();
  }, []);

  // Handle form input changes
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission (save changes)
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8080/lms/books/${formData.bookId}`, formData);
      setShowModal(false); // Close the modal after submitting
      navigate("/"); // Redirect to the book list page after editing
    } catch (error) {
      console.error("Error updating book:", error);
    }
  };

  // Open modal with book data when selected
  const openModal = (book) => {
    setFormData({
      bookId: book.bookId,
      bookName: book.bookName,
      description: book.description,
      authorName: book.authorName,
      isbnNumber: book.isbnNumber,
      edition: book.edition,
      category: book.category,
      isAvailable: book.available,
      location: book.location,
    });
    setShowModal(true);
  };

  // Close modal
  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Edit Books</h2>

      {/* Book List Table */}
      <table className="table-auto w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="px-4 py-2">Book Name</th>
            <th className="px-4 py-2">Author</th>
            <th className="px-4 py-2">Category</th>
            <th className="px-4 py-2">Location</th>
            <th className="px-4 py-2">Available</th>
            <th className="px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.bookId}>
              <td className="px-4 py-2">{book.bookName}</td>
              <td className="px-4 py-2">{book.authorName}</td>
              <td className="px-4 py-2">{book.category}</td>
              <td className="px-4 py-2">{book.location}</td>
              <td className="px-4 py-2">{book.available ? "Yes" : "No"}</td>
              <td className="px-4 py-2">
                <button
                  className="btn btn-primary"
                  onClick={() => openModal(book)} // Open modal with selected book data
                >
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal Overlay */}
      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-6 rounded-lg w-1/2">
            <h2 className="text-2xl font-semibold mb-4">Edit Book</h2>
            <form onSubmit={handleSubmit}>
              <div>
                <label>Book Name</label>
                <input
                  type="text"
                  name="bookName"
                  value={formData.bookName}
                  onChange={handleFormChange}
                  className="input input-bordered w-full mb-4"
                />
              </div>
              <div>
                <label>Description</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleFormChange}
                  className="textarea textarea-bordered w-full mb-4"
                />
              </div>
              <div>
                <label>Author</label>
                <input
                  type="text"
                  name="authorName"
                  value={formData.authorName}
                  onChange={handleFormChange}
                  className="input input-bordered w-full mb-4"
                />
              </div>
              <div>
                <label>Category</label>
                <input
                  type="text"
                  name="category"
                  value={formData.category}
                  onChange={handleFormChange}
                  className="input input-bordered w-full mb-4"
                />
              </div>
              <div>
                <label>Location</label>
                <input
                  type="text"
                  name="location"
                  value={formData.location}
                  onChange={handleFormChange}
                  className="input input-bordered w-full mb-4"
                />
              </div>
              <div>
                <label>Available</label>
                <input
                  type="checkbox"
                  name="isAvailable"
                  checked={formData.isAvailable}
                  onChange={(e) => setFormData({ ...formData, isAvailable: e.target.checked })}
                  className="checkbox"
                />
              </div>
              <button type="submit" className="btn btn-success mt-4">
                Save Changes
              </button>
              <button type="button" className="btn btn-secondary mt-4 ml-2" onClick={closeModal}>
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default EditBook;
