import React, { useEffect, useState } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [totalPages, setTotalPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [locations, setLocations] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState("MIC Campus");
  const [category, setCategory] = useState("All");
  const [searchValue, setSearchValue] = useState("");
  const [isEditMode, setIsEditMode] = useState(false);
  const [formData, setFormData] = useState({
    bookId: "",
    bookName: "",
    authorName: "",
    edition: 1,
    category: "",
    isAvailable: true,
    location: "",
    count: 1,
  });

  // Fetch initial data
  useEffect(() => {
    fetchLocations();
    fetchCategories();
    fetchBooks(selectedLocation, currentPage, "", category === "All" ? null : category);
  }, [selectedLocation, currentPage, category]);

  const fetchLocations = async () => {
    try {
      const response = await axios.get("http://localhost:8080/lms/books/locations");
      setLocations(response.data);
    } catch (error) {
      toast.error("Error fetching locations.");
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get("http://localhost:8080/lms/books/categories");
      setCategories(response.data);
    } catch (error) {
      toast.error("Error fetching categories.");
    }
  };

  const fetchBooks = async (location, page, searchQuery, category) => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:8080/lms/books/grouped", {
        params: {
          pageNumber: page,
          size: 10,
          locationFilter: location,
          categoryFilter: category,
          search: searchQuery,
        },
      });
      setBooks(response.data.books);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      toast.error("Error fetching books.");
    } finally {
      setLoading(false);
    }
  };

  const generateBookId = () => `BOOK-${Math.floor(Math.random() * 1000000)}`;

  const handleSearch = () => {
    fetchBooks(selectedLocation, 0, searchValue, category === "All" ? null : category);
    setCurrentPage(0);
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleAddBook = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:8080/lms/books/addBooks", formData);
      toast.success("Book added successfully!");
      fetchBooks(selectedLocation, currentPage, "", category === "All" ? null : category);
      setShowForm(false);
    } catch (error) {
      toast.error("Error adding book.");
    }
  };

  const handleEdit = (book) => {
    setFormData(book);
    setIsEditMode(true);
    setShowForm(true);
  };

  const handleSaveEdit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8080/lms/books/update/${formData.bookId}`, formData);
      toast.success("Book updated successfully!");
      setShowForm(false);
      setIsEditMode(false);
      fetchBooks(selectedLocation, currentPage, "", category === "All" ? null : category);
    } catch (error) {
      toast.error("Error updating book.");
    }
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setCurrentPage(newPage);
    }
  };

  const showAddBookForm = () => {
    setFormData({
      bookId: generateBookId(), // Auto-generate book ID for new book
      bookName: "",
      authorName: "",
      edition: 1,
      category: "",
      isAvailable: true,
      location: "",
      count: 1,
    });
    setIsEditMode(false);
    setShowForm(true);
  };

  return (
    <div className="container mx-auto p-6">
      <ToastContainer />
      <div className="flex justify-between items-center mb-4">
        <div>
          <select
            className="border p-2"
            value={selectedLocation}
            onChange={(e) => setSelectedLocation(e.target.value)}
          >
            {locations.map((loc) => (
              <option key={loc.locationId} value={loc.name}>
                {loc.name}
              </option>
            ))}
          </select>
          <select
            className="border p-2 ml-2"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          >
            <option value="All">All Categories</option>
            {categories.map((cat) => (
              <option key={cat.categoryId} value={cat.categoryName}>
                {cat.categoryName}
              </option>
            ))}
          </select>
        </div>
        <div>
          <input
            type="text"
            className="border p-2"
            placeholder="Search books..."
            value={searchValue}
            onChange={(e) => setSearchValue(e.target.value)}
          />
          <button onClick={handleSearch} className="bg-blue-500 text-white p-2 ml-2">
            Search
          </button>
        </div>
        <button onClick={showAddBookForm} className="bg-green-500 text-white p-2">
          Add Book
        </button>
      </div>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <table className="table-auto w-full border">
          <thead>
            <tr>
              <th>Book ID</th>
              <th>Name</th>
              <th>Author</th>
              <th>Edition</th>
              <th>Category</th>
              <th>Location</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {books.map((book) => (
              <tr key={book.bookId}>
                <td>{book.bookId}</td>
                <td>{book.bookName}</td>
                <td>{book.authorName}</td>
                <td>{book.edition}</td>
                <td>{book.category}</td>
                <td>{book.location}</td>
                <td>
                  <button onClick={() => handleEdit(book)} className="bg-yellow-500 text-white p-2">
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {showForm && (
        <div className="modal">
          <form onSubmit={isEditMode ? handleSaveEdit : handleAddBook}>
            <h2>{isEditMode ? "Edit Book" : "Add Book"}</h2>
            <input
              type="text"
              name="bookId"
              value={formData.bookId}
              readOnly // Book ID is read-only in the form
              className="border p-2"
            />
            <input
              type="text"
              name="bookName"
              placeholder="Book Name"
              value={formData.bookName}
              onChange={handleFormChange}
              className="border p-2"
            />
            <input
              type="text"
              name="authorName"
              placeholder="Author Name"
              value={formData.authorName}
              onChange={handleFormChange}
              className="border p-2"
            />
            <input
              type="number"
              name="edition"
              placeholder="Edition"
              value={formData.edition}
              onChange={handleFormChange}
              className="border p-2"
            />
            <select
              name="category"
              value={formData.category}
              onChange={handleFormChange}
              className="border p-2"
            >
              <option value="">Select Category</option>
              {categories.map((category) => (
               <option key={category.categoryId} value={category.categoryName}>
               {category.categoryName}
             </option>
              ))}
            </select>
            <select
              name="location"
              value={formData.location}
              onChange={handleFormChange}
              className="border p-2"
            >
              {locations.map((loc) => (
                <option key={loc.locationId} value={loc.name}>
                  {loc.name}
                </option>
              ))}
            </select>
            <input
              type="number"
              name="count"
              placeholder="Count"
              value={formData.count}
              onChange={handleFormChange}
              className="border p-2"
            />
            <button type="submit" className="bg-blue-500 text-white p-2">
              {isEditMode ? "Save Changes" : "Add Book"}
            </button>
            <button
              type="button"
              onClick={() => setShowForm(false)}
              className="bg-red-500 text-white p-2"
            >
              Cancel
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default BookList;
