import React from "react";
import { Outlet, Link } from "react-router-dom";
import "./UserDashboard.css"; // Your combined CSS file

const UserLayout = () => {
  const username = localStorage.getItem("username"); // Get username from storage

  const handleLogout = () => {
    localStorage.removeItem("username"); // Clear username
    window.location.href = "/login"; // Redirect to login
  };

  return (
    <>
      {/* Menu Bar */}
      <nav className="menu-bar">
        <div className="menu-links">
          <Link to="/userBookList">Home</Link>
          <Link to="/bookingList">My Bookings</Link>
          <Link to="/reservationList">My Reservations</Link>
        </div>
        <div>
          <span className="welcome-message">Welcome, {username || "User"}</span>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </nav>

      {/* Page Content */}
      <main className="container">
        <Outlet /> {/* Placeholder for nested routes */}
      </main>
    </>
  );
};

export default UserLayout;
