import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const ManageBooksLoanRequests = () => {
  const [loanRequests, setLoanRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBook, setSelectedBook] = useState(null);
  const [userId, setUserId] = useState(null);
  const [currentTab, setCurrentTab] = useState('loanRequests'); // Track which submenu is active

  // Fetch loan requests based on the status
  const fetchLoanRequestsByStatus = async (status) => {
    try {
      const response = await axios.get(`http://localhost:8080/lms/loan-reservation/loan-requests/${status}`);
      setLoanRequests(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch loan requests. Please try again later.');
      setLoading(false);
    }
  };

  // Fetch userId from localStorage or API
  const fetchUserId = async () => {
    const username = localStorage.getItem('username');
    const type = localStorage.getItem('role');

    try {
      const response = await fetch(`http://localhost:8080/lms/auth/getUserId?username=${username}&type=${type}`);
      const data = await response.json();
      if (data.userId) {
        setUserId(data.userId);
      } else {
        alert('User ID not found');
      }
    } catch (err) {
      alert('Error fetching userId');
    }
  };

  useEffect(() => {
    fetchUserId();
  }, []);

  useEffect(() => {
    if (currentTab === 'loanRequests') {
      fetchLoanRequestsByStatus('Preparing for Pickup');
    } else if (currentTab === 'pickupBooks') {
      fetchLoanRequestsByStatus('Ready for Pickup');
    } else if (currentTab === 'returnBooks') {
      fetchLoanRequestsByStatus('Loaned');
    } else if (currentTab === 'reservationRequests') {
      fetchLoanRequestsByStatus('Pending Reservation');
    } else if (currentTab === 'renewalRequests') {
      fetchLoanRequestsByStatus('Pending Renewal');
    }
  }, [currentTab]);

  const handleStatusChange = async (loanId, status) => {
    try {
      const response = await axios.put(
        `http://localhost:8080/lms/loan-reservation/updateCheckout/${loanId}`,
        null,
        {
          params: {
            status: status,
            librarianId: userId,
          },
        }
      );
      if (response.status === 200) {
        alert('Loan request status updated!');
        setLoanRequests((prev) =>
          prev.map((request) =>
            request.loanId === loanId ? { ...request, status: status } : request
          )
        );
      } else {
        alert('Failed to update the loan request status.');
      }
    } catch (error) {
      console.error('Error updating loan request status:', error);
      alert('Error updating loan request status. Please try again later.');
    }
  };

  const handleBookClick = (book) => {
    setSelectedBook(book);
  };

  const handleCloseModal = () => {
    setSelectedBook(null);
  };

  if (loading) {
    return <div>Loading loan requests...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-semibold mb-6">Manage Books Loan Requests</h1>

      {/* Navigation Bar */}
      <div className="mb-4">
        <nav className="flex space-x-4">
          <button
            onClick={() => setCurrentTab('loanRequests')}
            className={`px-4 py-2 rounded ${currentTab === 'loanRequests' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          >
            Loan Requests
          </button>
          <button
            onClick={() => setCurrentTab('pickupBooks')}
            className={`px-4 py-2 rounded ${currentTab === 'pickupBooks' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          >
            Pickup Books
          </button>
          <button
            onClick={() => setCurrentTab('returnBooks')}
            className={`px-4 py-2 rounded ${currentTab === 'returnBooks' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          >
            Return Books
          </button>
          <button
            onClick={() => setCurrentTab('reservationRequests')}
            className={`px-4 py-2 rounded ${currentTab === 'reservationRequests' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          >
            Reservation Requests
          </button>
          <button
            onClick={() => setCurrentTab('renewalRequests')}
            className={`px-4 py-2 rounded ${currentTab === 'renewalRequests' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          >
            Renewal Requests
          </button>
        </nav>
      </div>

      {/* Loan Requests Table */}
      <table className="min-w-full bg-white border border-gray-300 shadow-md rounded-lg">
        <thead>
          <tr className="bg-gray-100">
            <th className="px-6 py-3 text-left text-gray-600">Book Name</th>
            <th className="px-6 py-3 text-left text-gray-600">Author</th>
            <th className="px-6 py-3 text-left text-gray-600">User Name</th>
            <th className="px-6 py-3 text-left text-gray-600">Requested Date</th>
            <th className="px-6 py-3 text-left text-gray-600">Status</th>
            <th className="px-6 py-3 text-left text-gray-600">Actions</th>
          </tr>
        </thead>
        <tbody>
          {loanRequests.map((request) => (
            <tr key={request.loanId} className="border-t">
              <td className="px-6 py-3">
                <a
                  href="#"
                  onClick={() => handleBookClick(request)}
                  className="text-blue-500 hover:underline"
                >
                  {request.bookName}
                </a>
              </td>
              <td className="px-6 py-3">{request.authorName}</td>
              <td className="px-6 py-3">{request.userName}</td>
              <td className="px-6 py-3">{new Date(request.requestedDate).toLocaleString()}</td>
              <td className="px-6 py-3">{request.status}</td>
              <td className="px-6 py-3">
                {currentTab === 'loanRequests' && request.status === 'Preparing for Pickup' && (
                  <button
                    onClick={() => handleStatusChange(request.loanId, 'Ready for Pickup')}
                    className="bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded"
                  >
                    Ready for Pickup
                  </button>
                )}
                {currentTab === 'pickupBooks' && request.status === 'Ready for Pickup' && (
                  <button
                    onClick={() => handleStatusChange(request.loanId, 'Loaned')}
                    className="bg-green-500 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded"
                  >
                    Loaned
                  </button>
                )}
                {currentTab === 'pickupBooks' && request.status === 'Ready for Pickup' && (
                  <button
                    onClick={() => handleStatusChange(request.loanId, 'Cancelled')}
                    className="bg-red-500 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded"
                  >
                    Cancel
                  </button>
                )}
                {currentTab === 'returnBooks' && request.status === 'Loaned' && (
                  <button
                    onClick={() => handleStatusChange(request.loanId, 'Returned')}
                    className="bg-green-500 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded"
                  >
                    Returned
                  </button>
                )}
                {currentTab === 'reservationRequests' && request.status === 'Pending Reservation' && (
                  <>
                    <button
                      onClick={() => handleStatusChange(request.loanId, 'Reserved')}
                      className="bg-green-500 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded"
                    >
                      Approve
                    </button>
                    <button
                      onClick={() => handleStatusChange(request.loanId, 'Reservation Denied')}
                      className="bg-red-500 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded"
                    >
                      Deny
                    </button>
                  </>
                )}
                {currentTab === 'renewalRequests' && request.status === 'Pending Renewal' && (
                  <>
                    <button
                      onClick={() => handleStatusChange(request.loanId, 'Renewed')}
                      className="bg-green-500 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded"
                    >
                      Approve
                    </button>
                    <button
                      onClick={() => handleStatusChange(request.loanId, 'Renewal Denied')}
                      className="bg-red-500 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded"
                    >
                      Deny
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageBooksLoanRequests;
