import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BooksList = () => {
  const [books, setBooks] = useState([]);
  const [page, setPage] = useState(1);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get(`/api/books?page=${page}`);
        setBooks(response.data.books);
      } catch (error) {
        console.error("There was an error fetching the books data!", error);
      }
    };

    fetchBooks();
  }, [page]);

  const handleEdit = (id) => {
    // Implement edit logic
  };

  const handleDelete = (id) => {
    // Implement delete logic
  };

  return (
    <div>
      <h2 class="text-xl font-bold">Books</h2>
      <table class="table-auto w-full mt-4">
        <thead>
          <tr>
            <th>Book Name</th>
            <th>Description</th>
            <th>Author Name</th>
            <th>ISBN Number</th>
            <th>Edition</th>
            <th>Category</th>
            <th>Is Available</th>
            <th>Location</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.id}>
              <td>{book.name}</td>
              <td>{book.description}</td>
              <td>{book.author}</td>
              <td>{book.isbn}</td>
              <td>{book.edition}</td>
              <td>{book.category}</td>
              <td>{book.isAvailable ? 'Yes' : 'No'}</td>
              <td>{book.location}</td>
              <td>
                <button onClick={() => handleEdit(book.id)} class="btn btn-primary">Edit</button>
                <button onClick={() => handleDelete(book.id)} class="btn btn-danger">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div class="mt-4">
        <button onClick={() => setPage(page - 1)} disabled={page === 1} class="btn btn-secondary">Previous</button>
        <button onClick={() => setPage(page + 1)} class="btn btn-secondary">Next</button>
      </div>
    </div>
  );
};

export default BooksList;
