import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MembersList = () => {
  const [members, setMembers] = useState([]);
  const [page, setPage] = useState(1);

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const response = await axios.get(`/api/members?page=${page}`);
        setMembers(response.data.members);
      } catch (error) {
        console.error("There was an error fetching the members data!", error);
      }
    };

    fetchMembers();
  }, [page]);

  return (
    <div>
      <h2 class="text-xl font-bold">Members</h2>
      <table class="table-auto w-full mt-4">
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
          </tr>
        </thead>
        <tbody>
          {members.map((member) => (
            <tr key={member.id}>
              <td>{member.firstName}</td>
              <td>{member.lastName}</td>
              <td>{member.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <div class="mt-4">
        <button onClick={() => setPage(page - 1)} disabled={page === 1} class="btn btn-secondary">Previous</button>
        <button onClick={() => setPage(page + 1)} class="btn btn-secondary">Next</button>
      </div>
    </div>
  );
};

export default MembersList;
