import React, { useState } from 'react';
import { FaUser, FaBook, FaClipboardList, FaSignOutAlt, FaBars } from 'react-icons/fa';
import { useNavigate, Outlet, Link } from 'react-router-dom';

const LibrarianDashboard = () => {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const toggleSidebar = () => {
    setIsCollapsed(!isCollapsed);
  };

  const navigate = useNavigate();
  const username = localStorage.getItem('username') || 'Librarian';

  const handleLogout = () => {
    navigate('/login', { replace: true });
  };

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div
        className={`bg-gray-800 text-white transition-all duration-300 ${
          isCollapsed ? 'w-16' : 'w-56'
        }`}
      >
        {/* Sidebar Header */}
        <div className="flex items-center justify-between p-4">
          <FaBars
            className="cursor-pointer text-xl"
            onClick={toggleSidebar}
          />
        </div>

        {/* Menu Items */}
        <div className="mt-10">
          {/* Welcome */}
          <div className="flex items-center p-4 hover:bg-gray-700 cursor-pointer">
            <FaUser size={20} />
            {!isCollapsed && <span className="ml-4">Hello, {username}</span>}
          </div>

          {/* Loan Requests */}
          <div
            className="flex items-center p-4 hover:bg-gray-700 cursor-pointer"
            onClick={() => navigate('/manage-loan-requests')}
          >
            <FaBook size={20} />
            {!isCollapsed && <span className="ml-4">Loan Requests</span>}
          </div>

          {/* Manage Resources */}
          <div
            className="flex items-center p-4 hover:bg-gray-700 cursor-pointer"
            onClick={() => navigate('/books')}
          >
            <FaClipboardList size={20} />
            {!isCollapsed && <span className="ml-4">Manage Resources</span>}
          </div>

          {/* Logout */}
          <div
            className="flex items-center p-4 hover:bg-gray-700 cursor-pointer"
            onClick={handleLogout}
          >
            <FaSignOutAlt size={20} />
            {!isCollapsed && <span className="ml-4">Logout</span>}
          </div>
        </div>
      </div>
      <nav className="menu-bar">
      <div className="menu-links">
        <Link 
          to="/loan-requests" 
          className={location.pathname === '/loan-requests' ? 'active' : ''}>
          Loan Requests
        </Link>
        <Link 
          to="/bookingList" 
          className={location.pathname === '/bookingList' ? 'active' : ''}>
          My Bookings
        </Link>
        <Link 
          to="/reservationList" 
          className={location.pathname === '/reservationList' ? 'active' : ''}>
          My Reservations
        </Link>
      </div>
    </nav>
      {/* Main Content */}
      <main className="flex-grow p-6">
        <Outlet />
      </main>
    </div>
  );
};

export default LibrarianDashboard;
