import React, { useEffect, useState } from "react";
import axios from "axios";

const BooksList = () => {
  const [books, setBooks] = useState([]);
  const [showRequestForm, setShowRequestForm] = useState(false);
  const [showReservationForm, setShowReservationForm] = useState(false);
  const [selectedBook, setSelectedBook] = useState(null);

  // Fetch books list
  const fetchBooks = async () => {
    try {
      const response = await axios.get("http://localhost:8080/lms/books");
      setBooks(response.data);
    } catch (error) {
      console.error("Error fetching books:", error);
    }
  };

  // Handle Checkout (Open Request Form)
  const handleCheckout = (book) => {
    setSelectedBook(book);
    setShowRequestForm(true);
  };

  // Handle Reserve (Open Reservation Form)
  const handleReserve = (book) => {
    setSelectedBook(book);
    setShowReservationForm(true);
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Books List</h2>
      <table className="table-auto w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="px-4 py-2">Book Name</th>
            <th className="px-4 py-2">Author</th>
            <th className="px-4 py-2">Category</th>
            <th className="px-4 py-2">Available</th>
            <th className="px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.isbnNumber}>
              <td className="px-4 py-2">{book.bookName}</td>
              <td className="px-4 py-2">{book.authorName}</td>
              <td className="px-4 py-2">{book.category}</td>
              <td className="px-4 py-2">
                {book.isAvailable ? "Available" : "Not Available"}
              </td>
              <td className="px-4 py-2">
                {book.isAvailable ? (
                  <button
                    className="btn btn-primary mr-2"
                    onClick={() => handleCheckout(book)}
                  >
                    Checkout
                  </button>
                ) : (
                  <button
                    className="btn btn-secondary"
                    onClick={() => handleReserve(book)}
                  >
                    Reserve
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Request Form Modal */}
      {showRequestForm && (
        <div className="modal-overlay">
          <div className="modal-window">
            <h3>Checkout Book</h3>
            <p>Book Name: {selectedBook?.bookName}</p>
            <form
              onSubmit={async (e) => {
                e.preventDefault();
                try {
                  await axios.post("http://localhost:8080/lms/book_loan_request_api", {
                    bookId: selectedBook.id,
                    userId: "currentUserId", // Replace with actual userId
                  });
                  alert("Book checked out successfully!");
                  setShowRequestForm(false);
                  fetchBooks(); // Refresh list
                } catch (error) {
                  console.error("Error checking out book:", error);
                }
              }}
            >
              <button type="submit" className="btn btn-primary">
                Confirm Checkout
              </button>
              <button
                type="button"
                className="btn btn-secondary ml-2"
                onClick={() => setShowRequestForm(false)}
              >
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Reservation Form Modal */}
      {showReservationForm && (
        <div className="modal-overlay">
          <div className="modal-window">
            <h3>Reserve Book</h3>
            <p>Book Name: {selectedBook?.bookName}</p>
            <form
              onSubmit={async (e) => {
                e.preventDefault();
                try {
                  await axios.post("http://localhost:8080/lms/book_loan_reservet_api", {
                    bookId: selectedBook.id,
                    userId: "currentUserId", // Replace with actual userId
                  });
                  alert("Book reserved successfully!");
                  setShowReservationForm(false);
                  fetchBooks(); // Refresh list
                } catch (error) {
                  console.error("Error reserving book:", error);
                }
              }}
            >
              <button type="submit" className="btn btn-primary">
                Confirm Reservation
              </button>
              <button
                type="button"
                className="btn btn-secondary ml-2"
                onClick={() => setShowReservationForm(false)}
              >
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default BooksList;
