import React, { useState } from "react";
import { login } from '../../services/Authservice';
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom"; 
import "./Auth.css";

const Login = () => {
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const response = await login(formData); 
      const { username, role } = response.data; 
      // Store username in localStorage
      localStorage.setItem('username', username);
      localStorage.setItem('role', role);
      setLoading(false);
      console.log(`Logged in as: ${username}, Role: ${role}`);

      // Redirect based on role
      if (role.toLowerCase() === "admin") navigate("/admin");
      else if (role.toLowerCase() === "librarian") navigate("/librarian");
      else if (role.toLowerCase() === "user") navigate("/user");
      else setError("Unexpected role. Please contact support.");
    } catch (err) {
      setError("Invalid username or password."); // Display error if login fails
    }
  };

  return (
  <div className="auth-container">
              <div className="auth-header">
                  Book<span>Nest</span>
              </div>
              <div className="auth-form">
                  <form onSubmit={handleSubmit}>
                      <div className="form-group">
                          <h2 className="form-title">Sign Up</h2>
                      </div>
                      <div className="form-group">
                          <label>Username:</label>
                          <input
                            type="text"
                            name="username"
                            value={formData.username}
                            onChange={handleInputChange}
                            required
                          />
                    </div>
                    <div className="form-group">
                      <label>Password:</label>
                      <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleInputChange}
                        required
                      />
                    </div>
                    {/* Submit button */}
                    <button type="submit" className="submit-btn" disabled={loading}>
                        {loading ? 'Logging in...' : 'Login In'}
                    </button>
                    {/* Add Sign Up link */}
                    <p class="text-center text-gray-600">
                      Don't have an account? 
                      <Link to="/signup" class="text-blue-500 hover:text-blue-700 font-semibold">
                        Sign up here
                      </Link>
                    </p>

                  </form>


                </div>
  </div>
  );
};

export default Login;
