import React, { useEffect, useState } from "react";
import axios from "axios";
import './UserDashboard.css'; // Assuming you have the required CSS for modal and table

const BookModal = ({ isOpen, onClose, book, onSubmit, formType, formData, setFormData }) => {
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData); // Submitting the formData to parent
  };

  const handleClose = () => {
    setFormData({
      firstName: "",
      lastName: "",
      studentId: "",
      reservationDate: "",
    });
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-window">
        <h2 className="text-2xl font-semibold mb-4">{formType} Book</h2>
        <p><strong>Book Name:</strong> {book.bookName}</p>
        <p><strong>Description:</strong> {book.description}</p>
        <p><strong>Author:</strong> {book.authorName}</p>
        <p><strong>ISBN:</strong> {book.isbnNumber}</p>
        <p><strong>Edition:</strong> {book.edition}</p>
        <p><strong>Category:</strong> {book.category}</p>

        <form onSubmit={handleSubmit} className="modal-form">
          <label>
            First Name:
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              className="block w-full mb-2 p-2 border"
              required
            />
          </label>
          <label>
            Last Name:
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              className="block w-full mb-2 p-2 border"
              required
            />
          </label>
          <label>
            Student ID (700#):
            <input
              type="text"
              name="studentId"
              value={formData.studentId}
              onChange={handleChange}
              className="block w-full mb-2 p-2 border"
              required
            />
          </label>
          {formType === "Reserve" && (
            <label>
              Reservation Date:
              <input
                type="date"
                name="reservationDate"
                value={formData.reservationDate}
                onChange={handleChange}
                min={new Date().toISOString().split("T")[0]}
                max={new Date(new Date().setDate(new Date().getDate() + 30)).toISOString().split("T")[0]}
                className="block w-full mb-2 p-2 border"
                required
              />
            </label>
          )}
          <button type="submit" className="mt-4 bg-blue-500 text-white py-2 px-4 rounded">
            {formType}
          </button>
        </form>
        <button onClick={handleClose} className="mt-4 text-gray-500">Close</button>
      </div>
    </div>
  );
};

const UserBookList = () => {
  const [books, setBooks] = useState([]);
  const [locations, setLocations] = useState([]);
  const [userId, setUserId] = useState(null);
  const [selectedLocation, setSelectedLocation] = useState("MIC Campus");
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [bookForModal, setBookForModal] = useState(null);
  const [formType, setFormType] = useState("");
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    studentId: "",
    reservationDate: "",
  });

  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const booksPerPage = 10;

  useEffect(() => {
    fetchLocations();
    fetchUserId();
  }, []);

  useEffect(() => {
    fetchBooks(selectedLocation, currentPage);
  }, [selectedLocation, currentPage]);

  const fetchLocations = async () => {
    try {
      const response = await axios.get("http://localhost:8080/lms/books/locations");
      setLocations(response.data);
    } catch (error) {
      console.error("Error fetching locations:", error);
    }
  };

  const fetchBooks = async (location, page) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8080/lms/books/grouped?page=${page}&size=${booksPerPage}&location=${location}`
      );
      setBooks(response.data.books);
      setTotalPages(response.data.totalPages); // Update total pages for pagination
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  const fetchUserId = async () => {
    const username = localStorage.getItem('username');
    const type = localStorage.getItem('role');
    try {
        const response = await fetch(`http://localhost:8080/lms/auth/getUserId?username=${username}&type=${type}`);
        const data = await response.json();
        if (data.userId) {
            setUserId(data.userId);
        } else {
          alert('User ID not found');
        }
    } catch (err) {
      alert('Error fetching userId');
    }
  };

  const handleLocationChange = (e) => {
    const selectedLocation = e.target.value;
    setSelectedLocation(selectedLocation);
    setCurrentPage(0); // Reset to first page when location changes
  };

  const openModal = (book, type) => {
    setBookForModal(book);
    setFormType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setBookForModal(null);
    setFormData({
      firstName: "",
      lastName: "",
      studentId: "",
      reservationDate: "",
    });
  };

  const handleCheckout = async (formData) => {
    try {
      const response = await axios.post('http://localhost:8080/lms/loan-reservation/requestCheckout', {
        isbn: bookForModal.isbnNumber,
        userId: userId,
      });
      if (response.status === 200) {
        alert('Checkout Successful');
      } else {
        alert('Something went wrong during checkout');
      }
    } catch (error) {
      console.error("Checkout API error:", error);
      alert('Error during checkout. Please try again later.');
    } finally {
      closeModal();
    }
  };

  const handleReservation = async (formData) => {
    try {
      const reservationDate = new Date(formData.reservationDate).toISOString();
      const response = await axios.post('http://localhost:8080/lms/loan-reservation/requestReservation', {
        isbn: bookForModal.isbnNumber,
        userId: userId,
        reservationDate: reservationDate,
      });
      if (response.status === 200) {
        alert('Reservation Successful');
      } else {
        alert('Something went wrong during reservation');
      }
    } catch (error) {
      console.error("Reservation API error:", error);
      alert('Error during reservation. Please try again later.');
    } finally {
      closeModal();
    }
  };

  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      setCurrentPage(newPage);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Book List</h2>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          <div className="mb-4">
            <label htmlFor="location" className="mr-2">
              Select Location:
            </label>
            <select
              id="location"
              value={selectedLocation}
              onChange={handleLocationChange}
              className="px-4 py-2 border"
            >
              {locations.map((location) => (
                <option key={location.locationId} value={location.name}>
                  {location.name}
                </option>
              ))}
            </select>
          </div>

          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2">Book Name</th>
                <th className="px-4 py-2">Description</th>
                <th className="px-4 py-2">Edition</th>
                <th className="px-4 py-2">Author</th>
                <th className="px-4 py-2">ISBN</th>
                <th className="px-4 py-2">Category</th>
                <th className="px-4 py-2">Available Count</th>
                <th className="px-4 py-2">Action</th>
              </tr>
            </thead>
            <tbody>
              {books.map((book) => (
                <tr key={book.isbnNumber}>
                  <td className="px-4 py-2">{book.bookName}</td>
                  <td className="px-4 py-2">{book.description}</td>
                  <td className="px-4 py-2">{book.edition}</td>
                  <td className="px-4 py-2">{book.authorName}</td>
                  <td className="px-4 py-2">{book.isbnNumber}</td>
                  <td className="px-4 py-2">{book.category}</td>
                  <td className="px-4 py-2">{book.availableCount}</td>
                  <td className="px-4 py-2">
                    {book.availableCount > 0 ? (
                      <button
                        onClick={() => openModal(book, "Checkout")}
                        className="bg-blue-500 text-white py-2 px-4 rounded"
                      >
                        Checkout
                      </button>
                    ) : (
                      <button
                        onClick={() => openModal(book, "Reserve")}
                        className="bg-green-500 text-white py-2 px-4 rounded"
                      >
                        Reserve
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="pagination mt-4">
            <button
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 0}
              className="px-4 py-2 bg-gray-300 text-white rounded"
            >
              Previous
            </button>
            <span className="px-4 py-2">{`Page ${currentPage + 1} of ${totalPages}`}</span>
            <button
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages - 1}
              className="px-4 py-2 bg-gray-300 text-white rounded"
            >
              Next
            </button>
          </div>
        </>
      )}

      <BookModal
        isOpen={isModalOpen}
        onClose={closeModal}
        book={bookForModal}
        onSubmit={formType === "Reserve" ? handleReservation : handleCheckout}
        formType={formType}
        formData={formData}
        setFormData={setFormData}
      />
    </div>
  );
};

export default UserBookList;
