import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ManageBooksLoanRequests = () => {
  const [loanRequests, setLoanRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBook, setSelectedBook] = useState(null);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    // Fetch all loan requests regardless of status
    const fetchLoanRequests = async () => {
      try {
        const response = await axios.get('http://localhost:8080/lms/loan-reservation/loan-requests');
        setLoanRequests(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch loan requests. Please try again later.');
        setLoading(false);
      }
    };

    // Fetch userId from localStorage or API
    const fetchUserId = async () => {
      const username = localStorage.getItem('username');
      const type = localStorage.getItem('role');

      try {
        const response = await fetch(`http://localhost:8080/lms/auth/getUserId?username=${username}&type=${type}`);
        const data = await response.json();
        if (data.userId) {
          setUserId(data.userId);
        } else {
          alert('User ID not found');
        }
      } catch (err) {
        alert('Error fetching userId');
      }
    };

    fetchLoanRequests();
    fetchUserId();
  }, []);

  const handleStatusChange = async (loanId, status) => {
    try {
      const response = await axios.put(
        `http://localhost:8080/lms/loan-reservation/updateCheckout/${loanId}`,
        null,
        {
          params: {
            status: status,
            librarianId: userId,
          },
        }
      );
      if (response.status === 200) {
        alert('Loan request status updated!');
        setLoanRequests((prev) =>
          prev.map((request) =>
            request.loanId === loanId ? { ...request, status: status } : request
          )
        );
      } else {
        alert('Failed to update the loan request status.');
      }
    } catch (error) {
      console.error('Error updating loan request status:', error);
      alert('Error updating loan request status. Please try again later.');
    }
  };

  const handleBookClick = (book) => {
    setSelectedBook(book);
  };

  const handleCloseModal = () => {
    setSelectedBook(null);
  };

  if (loading) {
    return <div>Loading loan requests...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-3xl font-semibold mb-6">Manage Books Loan Requests</h1>

      {loanRequests.length === 0 ? (
        <div className="text-center text-xl">No loan requests available.</div>
      ) : (
        <table className="min-w-full bg-white border border-gray-300 shadow-md rounded-lg">
          <thead>
            <tr className="bg-gray-100">
              <th className="px-6 py-3 text-left text-gray-600">Book Name</th>
              <th className="px-6 py-3 text-left text-gray-600">Author</th>
              <th className="px-6 py-3 text-left text-gray-600">User Name</th>
              <th className="px-6 py-3 text-left text-gray-600">Requested Date</th>
              <th className="px-6 py-3 text-left text-gray-600">Status</th>
              <th className="px-6 py-3 text-left text-gray-600">Actions</th>
            </tr>
          </thead>
          <tbody>
            {loanRequests.map((request) => (
              <tr key={request.loanId} className="border-t">
                <td className="px-6 py-3">
                  <a
                    href="#"
                    onClick={() => handleBookClick(request)}
                    className="text-blue-500 hover:underline"
                  >
                    {request.bookName}
                  </a>
                </td>
                <td className="px-6 py-3">{request.authorName}</td>
                <td className="px-6 py-3">{request.userName}</td>
                <td className="px-6 py-3">{new Date(request.requestedDate).toLocaleString()}</td>
                <td className="px-6 py-3">
                  <span
                    className={`inline-block px-3 py-1 text-xs font-semibold rounded-full ${
                      request.status === 'Pending'
                        ? 'bg-yellow-200 text-yellow-800'
                        : request.status === 'Ready for Pickup'
                        ? 'bg-blue-200 text-blue-800'
                        : request.status === 'Loaned'
                        ? 'bg-green-200 text-green-800'
                        : 'bg-red-200 text-red-800'
                    }`}
                  >
                    {request.status}
                  </span>
                </td>
                <td className="px-6 py-3">
                  <div className="space-x-3">
                    {request.status === 'Pending' && (
                      <button
                        onClick={() => handleStatusChange(request.loanId, 'Ready for Pickup')}
                        className="bg-blue-500 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded"
                      >
                        Ready for Pickup
                      </button>
                    )}
                    {request.status === 'Ready for Pickup' && (
                      <button
                        onClick={() => handleStatusChange(request.loanId, 'Loaned')}
                        className="bg-green-500 hover:bg-green-700 text-white font-semibold py-2 px-4 rounded"
                      >
                        Loaned
                      </button>
                    )}
                    {request.status !== 'Cancelled' && (
                      <button
                        onClick={() => handleStatusChange(request.loanId, 'Cancelled')}
                        className="bg-red-500 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded"
                      >
                        Cancelled
                      </button>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Modal to display complete book details */}
      {selectedBook && (
        <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-6 rounded-lg w-1/3">
            <h2 className="text-xl font-bold mb-4">{selectedBook.bookName}</h2>
            <p><strong>Author:</strong> {selectedBook.authorName}</p>
            <p><strong>Category:</strong> {selectedBook.category}</p>
            <p><strong>ISBN:</strong> {selectedBook.isbnNumber}</p>
            <p><strong>Edition:</strong> {selectedBook.edition}</p>
            <p><strong>Description:</strong> {selectedBook.description}</p>
            <p><strong>Location:</strong> {selectedBook.location || 'Not specified'}</p>
            <button
              onClick={handleCloseModal}
              className="mt-4 bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-4 rounded"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManageBooksLoanRequests;
