import React from 'react';
import { Pie, Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  ArcElement,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from 'chart.js';

// Register required Chart.js components
ChartJS.register(ArcElement, CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend);

// Sample data for Pie chart (Book Availability)
const pieData = {
  labels: ['Available Books', 'Loaned Books', 'Reserved Books'],
  datasets: [
    {
      data: [300, 50, 100],
      backgroundColor: ['#36A2EB', '#FF6384', '#FFCE56'],
      hoverBackgroundColor: ['#36A2EB', '#FF6384', '#FFCE56'],
    },
  ],
};

// Sample data for Line chart (Weekly Activity)
const lineData = {
  labels: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'],
  datasets: [
    {
      label: 'Books Loaned',
      data: [5, 10, 15, 20, 25, 30, 35],
      borderColor: '#36A2EB',
      backgroundColor: 'rgba(54, 162, 235, 0.2)',
      fill: true,
    },
    {
      label: 'Books Reserved',
      data: [3, 5, 8, 13, 18, 25, 30],
      borderColor: '#FF6384',
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      fill: true,
    },
  ],
};

// Tailwind CSS styled component
const AdminInsights = () => {
  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Dashboard Header */}
        <div className="text-center mb-6">
          <h1 className="text-4xl font-bold text-gray-800">Admin Insights Dashboard</h1>
          <p className="text-gray-600">Visualize key library statistics and performance metrics</p>
        </div>

        {/* Cards Section */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white shadow-md rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-gray-800">Total Books</h2>
            <p className="text-4xl font-bold text-blue-600">500</p>
          </div>
          <div className="bg-white shadow-md rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-gray-800">Active Loans</h2>
            <p className="text-4xl font-bold text-green-600">50</p>
          </div>
          <div className="bg-white shadow-md rounded-lg p-6 text-center">
            <h2 className="text-2xl font-semibold text-gray-800">Total Reservations</h2>
            <p className="text-4xl font-bold text-yellow-600">100</p>
          </div>
        </div>

        {/* Charts Section */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Pie Chart */}
          <div className="bg-white shadow-md rounded-lg p-6">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">Books Status Distribution</h3>
            <Pie data={pieData} />
          </div>

          {/* Line Chart */}
          <div className="bg-white shadow-md rounded-lg p-6">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">Weekly Activity</h3>
            <Line data={lineData} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminInsights;
