import React, { useEffect, useState } from "react";
import axios from "axios";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const UserBookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [showForm, setShowForm] = useState(false);
  const [formType, setFormType] = useState(""); // "loan" or "reserve"
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    studentId: "",
    bookName: "",
    authorName: "",
    isbnNumber: "",
    expectedDate: null, // For reservation
  });

  // Fetch books from the API
  const fetchBooks = async (page = 0) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8080/lms/books/grouped?page=${page}&size=10`
      );
      setBooks(response.data.books || []);
      setTotalPages(response.data.totalPages || 0);
      setCurrentPage(page);
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      fetchBooks(newPage);
    }
  };

  // Show form for loan or reservation
  const openForm = (book, type) => {
    setFormType(type);
    setFormData({
      firstName: "",
      lastName: "",
      studentId: "",
      bookName: book.bookName,
      authorName: book.authorName,
      isbnNumber: book.isbnNumber,
      expectedDate: null,
    });
    setShowForm(true);
  };

  // Handle form submission
  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const endpoint =
        formType === "loan"
          ? "http://localhost:8080/lms/book_loan_request_api"
          : "http://localhost:8080/lms/book_loan_reserve_api";

      await axios.post(endpoint, formData);
      alert(
        formType === "loan"
          ? "Book loan request submitted successfully!"
          : "Book reservation request submitted successfully!"
      );
      setShowForm(false);
      fetchBooks(currentPage); // Refresh books list
    } catch (error) {
      console.error("Error submitting the form:", error);
      alert("Failed to process your request. Please try again.");
    }
  };

  // Handle form field changes
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle date picker change
  const handleDateChange = (date) => {
    setFormData({
      ...formData,
      expectedDate: date,
    });
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Available Books</h2>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2">Book Name</th>
                <th className="px-4 py-2">Author</th>
                <th className="px-4 py-2">ISBN</th>
                <th className="px-4 py-2">Available Count</th>
                <th className="px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.map((book) => (
                <tr key={book.isbnNumber}>
                  <td className="px-4 py-2">{book.bookName}</td>
                  <td className="px-4 py-2">{book.authorName}</td>
                  <td className="px-4 py-2">{book.isbnNumber}</td>
                  <td className="px-4 py-2">{book.availableCount}</td>
                  <td className="px-4 py-2">
                    {book.availableCount > 0 ? (
                      <button
                        className="btn btn-primary mr-2"
                        onClick={() => openForm(book, "loan")}
                      >
                        Loan
                      </button>
                    ) : (
                      <button
                        className="btn btn-warning"
                        onClick={() => openForm(book, "reserve")}
                      >
                        Reserve
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination */}
          <div className="flex justify-between items-center mt-4">
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage - 1)}
              disabled={currentPage === 0}
            >
              Previous
            </button>
            <span className="text-lg">
              Page {currentPage + 1} of {totalPages}
            </span>
            <button
              className="btn btn-primary"
              onClick={() => handlePageChange(currentPage + 1)}
              disabled={currentPage === totalPages - 1}
            >
              Next
            </button>
          </div>

          {/* Modal for Loan/Reserve Form */}
          {showForm && (
            <div className="modal-overlay">
              <div className="modal-window">
                <h3 className="modal-title">
                  {formType === "loan" ? "Loan Book" : "Reserve Book"}
                </h3>
                <form onSubmit={handleFormSubmit}>
                  <input
                    type="text"
                    name="bookName"
                    value={formData.bookName}
                    placeholder="Book Name"
                    disabled
                    className="form-input"
                  />
                  <input
                    type="text"
                    name="authorName"
                    value={formData.authorName}
                    placeholder="Author Name"
                    disabled
                    className="form-input"
                  />
                  <input
                    type="text"
                    name="isbnNumber"
                    value={formData.isbnNumber}
                    placeholder="ISBN"
                    disabled
                    className="form-input"
                  />
                  <input
                    type="text"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleFormChange}
                    placeholder="First Name"
                    required
                    className="form-input"
                  />
                  <input
                    type="text"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleFormChange}
                    placeholder="Last Name"
                    required
                    className="form-input"
                  />
                  <input
                    type="text"
                    name="studentId"
                    value={formData.studentId}
                    onChange={handleFormChange}
                    placeholder="700# (Student ID)"
                    required
                    className="form-input"
                  />
                  {formType === "reserve" && (
                    <div>
                      <label className="block font-semibold mt-4">
                        Expected Availability Date:
                      </label>
                      <DatePicker
                        selected={formData.expectedDate}
                        onChange={handleDateChange}
                        minDate={new Date()}
                        placeholderText="Select a date"
                        className="form-input"
                        required
                      />
                    </div>
                  )}
                  <div className="modal-actions">
                    <button
                      type="button"
                      className="btn btn-secondary"
                      onClick={() => setShowForm(false)}
                    >
                      Cancel
                    </button>
                    <button type="submit" className="btn btn-primary">
                      Submit
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default UserBookList;
