import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const EditBook = () => {
  const { isbn, location } = useParams();
  const [books, setBooks] = useState([]); // State to store the list of books
  const [selectedBooks, setSelectedBooks] = useState([]); // State to store selected books for group edit
  const [formData, setFormData] = useState({
    bookName: "",
    description: "",
    authorName: "",
    category: "",
    isbnNumber: "",
  });
  const [showModal, setShowModal] = useState(false); // State to handle modal visibility
  const navigate = useNavigate();

  // Fetch the list of books
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/lms/books/list?isbn=${isbn}&location=${location}`);
        const booksData = Array.isArray(response.data) ? response.data : [];
        setBooks(booksData); // Safely set books state to an array
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };
    fetchBooks();
  }, [isbn, location]);

  // Handle form input changes for group edit
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle selection of books for group edit
  const handleSelectBook = (bookId) => {
    setSelectedBooks((prevSelectedBooks) => {
      if (prevSelectedBooks.includes(bookId)) {
        return prevSelectedBooks.filter((id) => id !== bookId); // Deselect
      } else {
        return [...prevSelectedBooks, bookId]; // Select
      }
    });
  };

  // Handle form submission for group edit
  const handleSubmitGroupEdit = async (e) => {
    e.preventDefault();
    try {
      // Apply the form data changes to all selected books
      await Promise.all(
        selectedBooks.map(async (bookId) => {
          await axios.put(`http://localhost:8080/lms/books/${bookId}`, formData);
        })
      );
      setShowModal(false); // Close the modal after submitting
      setSelectedBooks([]); // Clear selected books
      navigate("/"); // Redirect to the book list page after editing
    } catch (error) {
      console.error("Error updating books:", error);
    }
  };

  // Open modal for group edit
  const openGroupEditModal = () => {
    if (selectedBooks.length > 0) {
      setShowModal(true);
    }
  };

  // Close modal
  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Edit Books</h2>

      {/* Book List Table */}
      <table className="table-auto w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-100">
            <th className="px-4 py-2">Select</th>
            <th className="px-4 py-2">Book Id</th>
            <th className="px-4 py-2">Book Name</th>
            <th className="px-4 py-2">Author</th>
            <th className="px-4 py-2">Category</th>
            <th className="px-4 py-2">Location</th>
            <th className="px-4 py-2">Available</th>
          </tr>
        </thead>
        <tbody>
          {books.map((book) => (
            <tr key={book.bookId}>
              <td className="px-4 py-2">
                <input
                  type="checkbox"
                  onChange={() => handleSelectBook(book.bookId)}
                  checked={selectedBooks.includes(book.bookId)}
                />
              </td>
              <td className="px-4 py-2">{book.bookId}</td>
              <td className="px-4 py-2">{book.bookName}</td>
              <td className="px-4 py-2">{book.authorName}</td>
              <td className="px-4 py-2">{book.category}</td>
              <td className="px-4 py-2">{book.location}</td>
              <td className="px-4 py-2">{book.available ? "Yes" : "No"}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Group Edit Button */}
      <button
        className="btn btn-primary mt-4"
        onClick={openGroupEditModal}
        disabled={selectedBooks.length === 0}
      >
        Edit Selected Books
      </button>

      {/* Modal Overlay */}
      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white p-6 rounded-lg w-1/2">
            <h2 className="text-2xl font-semibold mb-4">Group Edit Books</h2>
            <form onSubmit={handleSubmitGroupEdit}>
              <div>
                <label>Book Name</label>
                <input
                  type="text"
                  name="bookName"
                  value={formData.bookName}
                  onChange={handleFormChange}
                  className="input input-bordered w-full mb-4"
                />
              </div>
              <div>
                <label>Description</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleFormChange}
                  className="textarea textarea-bordered w-full mb-4"
                />
              </div>
              <div>
                <label>Author</label>
                <input
                  type="text"
                  name="authorName"
                  value={formData.authorName}
                  onChange={handleFormChange}
                  className="input input-bordered w-full mb-4"
                />
              </div>
              <div>
                <label>Category</label>
                <input
                  type="text"
                  name="category"
                  value={formData.category}
                  onChange={handleFormChange}
                  className="input input-bordered w-full mb-4"
                />
              </div>
              <div>
                <label>ISBN Number</label>
                <input
                  type="text"
                  name="isbnNumber"
                  value={formData.isbnNumber}
                  onChange={handleFormChange}
                  className="input input-bordered w-full mb-4"
                />
              </div>
              <button type="submit" className="btn btn-success mt-4">
                Save Changes
              </button>
              <button type="button" className="btn btn-secondary mt-4 ml-2" onClick={closeModal}>
                Cancel
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default EditBook;
