import React, { useEffect, useState } from "react";
import axios from "axios";

const UserBookList = () => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [locations, setLocations] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState(""); // No default location initially
  const [loading, setLoading] = useState(false);

  // Fetch books and locations from API
  useEffect(() => {
    fetchLocations(); // Fetch locations first
  }, []);

  // Fetch Locations
  const fetchLocations = async () => {
    try {
      const response = await axios.get("http://localhost:8080/lms/books/locations");
      setLocations(response.data); // Set locations data

      // Set the default location as the first location in the list
      if (response.data.length > 0) {
        const defaultLocation = response.data[0].name;
        setSelectedLocation(defaultLocation); // Set the default location
        fetchBooks(defaultLocation); // Fetch books based on the default location
      }
    } catch (error) {
      console.error("Error fetching locations:", error);
    }
  };

  // Fetch Books based on selected location
  const fetchBooks = async (location) => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:8080/lms/books/grouped?page=0&size=20`);
      setBooks(response.data.books); // Set books data
      filterBooksByLocation(location); // Apply the default location filter on books
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle location selection
  const handleLocationChange = (e) => {
    const selectedLocation = e.target.value;
    setSelectedLocation(selectedLocation);

    // Filter books based on selected location
    filterBooksByLocation(selectedLocation);
  };

  // Filter books based on selected location
  const filterBooksByLocation = (location) => {
    const filtered = books.filter((book) => book.location === location);
    setFilteredBooks(filtered);
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Book List</h2>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          {/* Location Filter */}
          <div className="mb-4">
            <label htmlFor="location" className="mr-2">Select Location:</label>
            <select
              id="location"
              value={selectedLocation}
              onChange={handleLocationChange}
              className="px-4 py-2 border"
            >
              {locations.map((location, index) => (
                <option key={index} value={location.name}>{location.name}</option>
              ))}
            </select>
          </div>

          {/* Book Table */}
          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2">Book Name</th>
                <th className="px-4 py-2">Description</th>
                <th className="px-4 py-2">Edition</th>
                <th className="px-4 py-2">Author</th>
                <th className="px-4 py-2">ISBN</th>
                <th className="px-4 py-2">Location</th>
                <th className="px-4 py-2">Category</th>
                <th className="px-4 py-2">Available Count</th>
              </tr>
            </thead>
            <tbody>
              {filteredBooks.map((book) => (
                <tr key={book.isbnNumber}>
                  <td className="px-4 py-2">{book.bookName}</td>
                  <td className="px-4 py-2">{book.description}</td>
                  <td className="px-4 py-2">{book.edition}</td>
                  <td className="px-4 py-2">{book.authorName}</td>
                  <td className="px-4 py-2">{book.isbnNumber}</td>
                  <td className="px-4 py-2">{book.location}</td>
                  <td className="px-4 py-2">{book.category}</td>
                  <td className="px-4 py-2">{book.availableCount}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
};

export default UserBookList;
