import React, { useState, useEffect } from 'react';
import axios from 'axios';

const IssueBooks = () => {
  const [loanRequests, setLoanRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch all loan requests with pending status
    const fetchLoanRequests = async () => {
      try {
        const response = await axios.get('http://localhost:8080/lms/loan-reservation/pending-requests');
        setLoanRequests(response.data);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch loan requests. Please try again later.');
        setLoading(false);
      }
    };

    fetchLoanRequests();
  }, []);

  const handleApprove = async (requestId) => {
    try {
      const response = await axios.post(`http://localhost:8080/lms/loan-reservation/approve-request/${requestId}`);
      if (response.status === 200) {
        alert('Loan request approved successfully!');
        // Update the list after approval
        setLoanRequests((prev) => prev.filter((request) => request.id !== requestId));
      } else {
        alert('Failed to approve the loan request.');
      }
    } catch (error) {
      console.error('Error approving loan request:', error);
      alert('Error approving loan request. Please try again later.');
    }
  };

  if (loading) {
    return <div>Loading loan requests...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Issue Books - Loan Requests</h1>
      {loanRequests.length === 0 ? (
        <div className="text-center">No pending loan requests.</div>
      ) : (
        <table className="table-auto w-full border border-gray-300">
          <thead>
            <tr className="bg-gray-200">
              <th className="border px-4 py-2">Request ID</th>
              <th className="border px-4 py-2">Book Title</th>
              <th className="border px-4 py-2">User Name</th>
              <th className="border px-4 py-2">User ID</th>
              <th className="border px-4 py-2">Action</th>
            </tr>
          </thead>
          <tbody>
            {loanRequests.map((request) => (
              <tr key={request.id}>
                <td className="border px-4 py-2">{request.id}</td>
                <td className="border px-4 py-2">{request.bookTitle}</td>
                <td className="border px-4 py-2">{request.userName}</td>
                <td className="border px-4 py-2">{request.userId}</td>
                <td className="border px-4 py-2 text-center">
                  <button
                    onClick={() => handleApprove(request.id)}
                    className="bg-green-500 hover:bg-green-700 text-white font-bold py-1 px-2 rounded"
                  >
                    Approve
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default IssueBooks;
