import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import './LibrarianDashboard';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [totalPages, setTotalPages] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [locations, setLocations] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState("MIC Campus");
  const [category, setCategory] = useState("All");
  const [searchTerm, setSearchTerm] = useState("");
  const [searchvalue, setSearchValue] = useState("");
  const [formData, setFormData] = useState({
    bookName: "",
    authorName: "",
    isbnNumber: "",
    edition: 1,
    category: "",
    isAvailable: true,
    location: "",
    count: 1, 
  });
  const [isAddingExistingBook, setIsAddingExistingBook] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchLocations();
    fetchCategories();
  }, []);

  useEffect(() => {
      fetchBooks(selectedLocation, currentPage, searchTerm, category == 'All' ? null : category);
  }, [selectedLocation, currentPage, searchTerm, category]);

  const fetchLocations = async () => {
    try {
      const response = await axios.get("http://localhost:8080/lms/books/locations");
      setLocations(response.data);
    } catch (error) {
      console.error("Error fetching locations:", error);
    }
  };

  const fetchCategories = async () => {
    try {
      const response = await axios.get("http://localhost:8080/lms/books/categories");
      setCategories(response.data);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const fetchBooks = async (location, page, searchQuery, category) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8080/lms/books/grouped`, 
        {
          params: {
            pageNumber:page,
            size: 10,
            locationFilter:location,
            categoryFilter: category,
            search: searchQuery, 
          },
        }
      );
      setBooks(response.data.books);
      setTotalPages(response.data.totalPages); 
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };
  const handleLocationChange = (e) => {
    const selectedLocation = e.target.value;
    setSelectedLocation(selectedLocation);
    setCurrentPage(0); // Reset to first page when location changes
  };

  const handleSearch = (e) => {
    setSearchTerm(searchvalue);
    setCurrentPage(0); // Reset to the first page when a search is performed
  };

  const handleCategoryChange = (e) => {
    setCategory(e.target.value);
    setCurrentPage(0); // Reset to the first page when a search is performed
  };
  // Handle page change
  const handlePageChange = (newPage) => {
    if (newPage >= 0 && newPage < totalPages) {
      fetchBooks(newPage);
    }
  };

  // Handle form change
  const handleFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  // Handle adding a new book or  new copies of existing book
  const handleAddBook = async (e) => {
    try {
      await axios.post("http://localhost:8080/lms/books/addBooks", formData);

      setFormData({
        bookName: "",
        description: "",
        authorName: "",
        isbnNumber: "",
        edition: 1,
        category: "",
        location: "",
        count: 1,
      });

      await fetchBooks(currentPage);
      alert("Books added successfully!");
      setShowForm(false);
    } catch (error) {
      console.error("Error adding book:", error);
    }
  };

  // Fetch book details by ISBN (for adding count to existing book)
  const handleLookupBook = async (isbnNumber) => {
    try {
      const response = await axios.get(`http://localhost:8080/lms/books/details/isbn/${isbnNumber}`);
      const book = response.data;
      if(book){
        setFormData({
          bookName: book.bookName,
          description: book.description,
          authorName: book.authorName,
          isbnNumber: book.isbnNumber,
          edition: book.edition,
          category: book.category,
          isAvailable: book.isAvailable,
          location: "",
          count: 1,
        });
      setIsAddingExistingBook(true);
      }else{
      setIsAddingExistingBook(false);
      alert("Book not found");}

    } catch (error) {
      console.error("Error fetching book:", error);
    }
  };

  // Navigate to the EditBook page
  const handleEdit = (isbn, location) => {
    navigate(`/editBook/${isbn}/${location}`);
  };

  // Navigate to the DeleteBook page
  const handleDelete = (isbn, location) => {
    navigate(`/deleteBook/${isbn}/${location}`);
  };

  // Show the modal for adding a new book or adding count to an existing book
  const showAddBookForm = () => {
    setFormData({
      bookName: "",
      description: "",
      authorName: "",
      isbnNumber: "",
      edition: 1,
      category: "",
      isAvailable: true,
      location: "",
      count: 1,
    });
    setIsAddingExistingBook(false);
    setShowForm(true);
  };

  useEffect(() => {
    fetchBooks();
  }, []);

  return (
    <div className="container mx-auto p-6">
      {loading ? (
        <div className="flex justify-center items-center min-h-screen">
          <img src="/loader.gif" alt="Loading..." className="w-16 h-16" />
        </div>
      ) : (
        <>
               <div className="flex items-center space-x-4 mb-4">
  {/* Search Bar */}
  <input
    type="text"
    placeholder="Search for a book..."
    value={searchvalue}
    onChange={(e) => setSearchValue(e.target.value)} 
    className="px-4 py-2 border w-64"
  />
  <button onClick={handleSearch} className="bg-blue-500 text-white py-2 px-4 rounded"
  >Search</button>

  {/* Location Filter */}
  <div className="flex items-center space-x-2">
    <label htmlFor="location" className="font-medium">
      Location:
    </label>
    <select
      id="location"
      value={selectedLocation}
      onChange={handleLocationChange}
      className="px-4 py-2 border"
    >
      {locations.map((location) => (
        <option key={location.locationId} value={location.name}>
          {location.name}
        </option>
      ))}
    </select>
  </div>

  {/* Category Filter */}
  <div className="flex items-center space-x-2">
    <label htmlFor="category" className="font-medium">
      Category:
    </label>
    <select
      id="category"
      value={category}
      onChange={handleCategoryChange}
      className="px-4 py-2 border"
    >
      <option value="All">All</option>
      {categories.map((category) => (
        <option key={category.categoryId} value={category.categoryName}>
          {category.categoryName}
        </option>
      ))}
    </select>
  </div>
  <div className="flex-grow"></div>   {/* Add Book Button */}
            <button onClick={showAddBookForm} className="bg-blue-500 text-white py-2 px-4 rounded "
  >Add New Book / Add Copies</button>
</div>


          {/* Book Table */}
          <table className="min-w-full bg-white shadow-md rounded-lg border border-gray-200">
  <thead className="bg-blue-500 text-white">
    <tr>
      <th className="px-6 py-3 text-left text-sm font-semibold">Book Name</th>
      <th className="px-6 py-3 text-left text-sm font-semibold">Author</th>
      <th className="px-6 py-3 text-left text-sm font-semibold">Category</th>
      <th className="px-6 py-3 text-left text-sm font-semibold">Available Count</th>
      <th className="px-6 py-3 text-left text-sm font-semibold">Loaned Count</th>
      <th className="px-6 py-3 text-left text-sm font-semibold">Total Count</th>
      <th className="px-6 py-3 text-left text-sm font-semibold">Actions</th>
    </tr>
  </thead>
  <tbody>
    {books.map((book, index) => (
      <tr
        key={book.isbnNumber}
        className={`border-t ${index % 2 === 0 ? 'bg-gray-50' : 'bg-white'} hover:bg-blue-50`}
      >
        <td className="px-6 py-3 text-sm text-gray-700">{book.bookName}</td>
        <td className="px-6 py-3 text-sm text-gray-700">{book.authorName}</td>
        <td className="px-6 py-3 text-sm text-gray-700">{book.category}</td>
        <td className="px-6 py-3 text-sm text-gray-700">{book.availableCount}</td>
        <td className="px-6 py-3 text-sm text-gray-700">{book.loanedCount}</td>
        <td className="px-6 py-3 text-sm text-gray-700">{book.totalCount}</td>
        <td className="px-6 py-3 text-sm text-gray-700">
          <button
            className="bg-blue-500 text-white py-1 px-4 rounded-lg hover:bg-blue-600 mr-2 shadow"
            onClick={() => handleEdit(book.isbnNumber, book.location)}
          >
            Edit
          </button>
          <button
            className="bg-red-500 text-white py-1 px-4 rounded-lg hover:bg-red-600 shadow"
            onClick={() => handleDelete(book.isbnNumber, book.location)}
          >
            Delete
          </button>
        </td>
      </tr>
    ))}
  </tbody>
</table>

          {/* Pagination */}
          <div className="flex items-center justify-center space-x-4 mt-4">
          <button
            onClick={() => handlePageChange(currentPage - 1)}
            className={`py-2 px-6 rounded-md transition-colors duration-300 ${
              currentPage === 0
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                : 'bg-blue-500 text-white hover:bg-blue-600'
            }`}
            disabled={currentPage === 0}
          >
            Previous
          </button>

          <span className="text-lg text-gray-700">
            Page {currentPage + 1} of {totalPages}
          </span>

          <button
            onClick={() => handlePageChange(currentPage + 1)}
            className={`py-2 px-6 rounded-md transition-colors duration-300 ${
              currentPage === totalPages - 1
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                : 'bg-blue-500 text-white hover:bg-blue-600'
            }`}
            disabled={currentPage === totalPages - 1}
          >
            Next
          </button>
        </div>

          {/* Modal for adding book or updating count */}
          {showForm && (
  <div className="modal-overlay fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
    <div className="modal-window bg-white p-6 rounded-lg shadow-lg max-w-lg w-full">
      <h3 className="text-2xl font-semibold mb-4">
        {isAddingExistingBook ? "Add Copies to Existing Book" : "Add New Book"}
      </h3>
      <form onSubmit={handleAddBook} className="space-y-6">

        {/* ISBN Number */}
        <div className="form-group flex items-center gap-4">
          <label htmlFor="isbnNumber" className="text-sm font-medium text-gray-700 w-1/4">
            ISBN Number
          </label>
          <input
            id="isbnNumber"
            type="text"
            name="isbnNumber"
            value={formData.isbnNumber}
            onChange={handleFormChange}
            placeholder="Enter ISBN Number"
            required
            disabled={isAddingExistingBook}
            className="w-3/4 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Lookup Button */}
        <div className="flex justify-between items-center">
  <button
    type="button"
    className="btn btn-secondary bg-gray-300 text-gray-800 py-2 px-4 rounded-lg ml-auto"
    onClick={() => handleLookupBook(formData.isbnNumber)}
  >
    Lookup Book
  </button>
</div>


        {/* Book Name */}
        <div className="form-group flex items-center gap-4">
          <label htmlFor="bookName" className="text-sm font-medium text-gray-700 w-1/4">
            Book Name
          </label>
          <input
            id="bookName"
            type="text"
            name="bookName"
            value={formData.bookName}
            onChange={handleFormChange}
            placeholder="Enter Book Name"
            required
            disabled={isAddingExistingBook}
            className="w-3/4 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Description */}
        <div className="form-group flex items-center gap-4">
          <label htmlFor="description" className="text-sm font-medium text-gray-700 w-1/4">
            Description
          </label>
          <input
            id="description"
            type="text"
            name="description"
            value={formData.description}
            onChange={handleFormChange}
            placeholder="Enter Description"
            required
            disabled={isAddingExistingBook}
            className="w-3/4 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Author Name */}
        <div className="form-group flex items-center gap-4">
          <label htmlFor="authorName" className="text-sm font-medium text-gray-700 w-1/4">
            Author Name
          </label>
          <input
            id="authorName"
            type="text"
            name="authorName"
            value={formData.authorName}
            onChange={handleFormChange}
            placeholder="Enter Author Name"
            required
            disabled={isAddingExistingBook}
            className="w-3/4 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Edition */}
        <div className="form-group flex items-center gap-4">
          <label htmlFor="edition" className="text-sm font-medium text-gray-700 w-1/4">
            Edition
          </label>
          <input
            id="edition"
            type="number"
            name="edition"
            value={formData.edition}
            onChange={handleFormChange}
            placeholder="Enter Edition"
            required
            disabled={isAddingExistingBook}
            className="w-3/4 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Category */}
        <div className="form-group flex items-center gap-4">
          <label htmlFor="category" className="text-sm font-medium text-gray-700 w-1/4">
            Category
          </label>
          <input
            id="category"
            type="text"
            name="category"
            value={formData.category}
            onChange={handleFormChange}
            placeholder="Enter Category"
            required
            disabled={isAddingExistingBook}
            className="w-3/4 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Location */}
        <div className="form-group flex items-center gap-4">
          <label htmlFor="location" className="text-sm font-medium text-gray-700 w-1/4">
            Location
          </label>
          <input
            id="location"
            type="text"
            name="location"
            value={formData.location}
            onChange={handleFormChange}
            placeholder="Enter Location"
            required
            className="w-3/4 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Count */}
        <div className="form-group flex items-center gap-4">
          <label htmlFor="count" className="text-sm font-medium text-gray-700 w-1/4">
            Count
          </label>
          <input
            id="count"
            type="number"
            name="count"
            value={formData.count}
            onChange={handleFormChange}
            placeholder="Enter Count"
            required
            className="w-3/4 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
          />
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end gap-4 mt-6">
          <button
            type="button"
            onClick={() => setShowForm(false)}
            className="btn btn-cancel bg-gray-500 text-white py-2 px-4 rounded-lg hover:bg-gray-600"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="btn btn-submit bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600"
          >
            Save
          </button>
        </div>
      </form>
    </div>
  </div>
)}

        </>
      )}
    </div>
  );
};

export default BookList;
