import React, { useEffect, useState } from "react";
import axios from "axios";

const UserBookList = () => {
  const [books, setBooks] = useState([]);
  const [locations, setLocations] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState("MIC Campus"); // Default to MIC Campus
  const [loading, setLoading] = useState(false);
  const [selectedBook, setSelectedBook] = useState(null);
  const [modalType, setModalType] = useState(""); // "checkout" or "reserve"

  // Fetch locations on component load
  useEffect(() => {
    fetchLocations();
  }, []);

  // Fetch Locations
  const fetchLocations = async () => {
    try {
      const response = await axios.get("http://localhost:8080/lms/books/locations");
      setLocations(response.data);
      fetchBooks("MIC Campus"); // Default to MIC Campus on load
    } catch (error) {
      console.error("Error fetching locations:", error);
    }
  };

  // Fetch Books based on selected location
  const fetchBooks = async (location) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8080/lms/books/grouped?page=0&size=10&location=${location}`
      );
      setBooks(response.data.books);
    } catch (error) {
      console.error("Error fetching books:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle location change
  const handleLocationChange = (e) => {
    const selectedLocation = e.target.value;
    setSelectedLocation(selectedLocation);
    fetchBooks(selectedLocation); // Fetch books for selected location
  };

  const handleCheckout = (book) => {
    // Logic to show the request form modal
    setSelectedBook(book);
    setModalType("checkout");
  };

  const handleReserve = (book) => {
    // Logic to show the reservation form modal
    setSelectedBook(book);
    setModalType("reserve");
  };

  const closeModal = () => {
    setSelectedBook(null);
    setModalType("");
  };

  const handleRequestSubmit = async (data) => {
    try {
      const response = await axios.post(
        "http://localhost:8080/lms/book_loan_request_api",
        data
      );
      alert("Book successfully requested!");
      closeModal();
    } catch (error) {
      console.error("Error requesting book:", error);
      alert("Failed to request the book.");
    }
  };

  const handleReservationSubmit = async (data) => {
    try {
      const response = await axios.post(
        "http://localhost:8080/lms/book_loan_reservet_api",
        data
      );
      alert("Book successfully reserved!");
      closeModal();
    } catch (error) {
      console.error("Error reserving book:", error);
      alert("Failed to reserve the book.");
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-semibold mb-4">Book List</h2>

      {loading ? (
        <div>Loading...</div>
      ) : (
        <>
          {/* Location Filter */}
          <div className="mb-4">
            <label htmlFor="location" className="mr-2">
              Select Location:
            </label>
            <select
              id="location"
              value={selectedLocation}
              onChange={handleLocationChange}
              className="px-4 py-2 border"
            >
              {locations.map((location) => (
                <option key={location.locationId} value={location.name}>
                  {location.name}
                </option>
              ))}
            </select>
          </div>

          {/* Book Table */}
          <table className="table-auto w-full border-collapse border border-gray-300">
            <thead>
              <tr className="bg-gray-100">
                <th className="px-4 py-2">Book Name</th>
                <th className="px-4 py-2">Description</th>
                <th className="px-4 py-2">Edition</th>
                <th className="px-4 py-2">Author</th>
                <th className="px-4 py-2">ISBN</th>
                <th className="px-4 py-2">Location</th>
                <th className="px-4 py-2">Category</th>
                <th className="px-4 py-2">Available Count</th>
                <th className="px-4 py-2">Action</th>
              </tr>
            </thead>
            <tbody>
              {books.map((book) => (
                <tr key={book.isbnNumber}>
                  <td className="px-4 py-2">{book.bookName}</td>
                  <td className="px-4 py-2">{book.description}</td>
                  <td className="px-4 py-2">{book.edition}</td>
                  <td className="px-4 py-2">{book.authorName}</td>
                  <td className="px-4 py-2">{book.isbnNumber}</td>
                  <td className="px-4 py-2">{book.location}</td>
                  <td className="px-4 py-2">{book.category}</td>
                  <td className="px-4 py-2">{book.availableCount}</td>
                  <td className="px-4 py-2">
                    {book.availableCount > 0 ? (
                      <button
                        onClick={() => handleCheckout(book)}
                        className="px-4 py-2 bg-green-500 text-white rounded"
                      >
                        Checkout
                      </button>
                    ) : (
                      <button
                        onClick={() => handleReserve(book)}
                        className="px-4 py-2 bg-blue-500 text-white rounded"
                      >
                        Reserve
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {/* Modals for Request and Reservation */}
      {modalType === "checkout" && (
        <RequestForm
          book={selectedBook}
          onSubmit={handleRequestSubmit}
          onClose={closeModal}
        />
      )}

      {modalType === "reserve" && (
        <ReservationForm
          book={selectedBook}
          onSubmit={handleReservationSubmit}
          onClose={closeModal}
        />
      )}
    </div>
  );
};

