import React, { useState } from 'react';
import { signUp } from '../../services/Authservice';
import './Auth.css'; // Importing existing auth CSS
import { useNavigate } from "react-router-dom";

const SignUpForm = () => {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        confirmPassword: '',  // Added confirmPassword field
        firstName: '',
        lastName: '',
        email: '',
        type: 'user',  // Default to 'user'
        addressLine1: '', // Added address fields
        addressLine2: '',
        city: '',
        state: '',
        postalCode: '',
    });

    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate(); // Hook for navigation

    // Handle form changes
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Reset error and success messages before submitting
        setErrorMessage('');
        setSuccessMessage('');

        // Check if passwords match
        if (formData.password !== formData.confirmPassword) {
            setErrorMessage("Passwords do not match.");
            return;
        }

        setLoading(true); // Show loading spinner before sending request

        try {
            const response = await signUp(formData);  // Assuming signUp is an API call
            setLoading(false); // Hide loading spinner
            setSuccessMessage('Sign-up successful! Redirecting to login...');

            // Redirect to login page after success
            setTimeout(() => {
                navigate('/login'); // Redirect to login page after a delay
            }, 2000); // Wait for 2 seconds before redirecting

        } catch (error) {
            setLoading(false); // Hide loading spinner
            if (error.response) {
                // Handle backend errors (like duplicate username)
                setErrorMessage(error.response.data);  // Display the backend error message
            } else {
                // Handle other errors (network issues, etc.)
                setErrorMessage("An unexpected error occurred. Please try again.");
            }
        }
    };

    return (
        <div className="auth-container">
            <div className="auth-header">
                Book<span>Nest</span>
            </div>
            <div className="auth-form">
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <h2 className="form-title">Sign Up</h2>
                    </div>

                    {/* First Name and Last Name in the same row */}
                    <div className="form-group name-row">
                        <div className="input-group">
                            <label>First Name:</label>
                            <input 
                                type="text" 
                                name="firstName" 
                                value={formData.firstName} 
                                onChange={handleChange} 
                                required 
                            />
                        </div>
                        <div className="input-group">
                            <label>Last Name:</label>
                            <input 
                                type="text" 
                                name="lastName" 
                                value={formData.lastName} 
                                onChange={handleChange} 
                                required 
                            />
                        </div>
                    </div>

                    {/* Username input */}
                    <div className="form-group">
                        <label>Username:</label>
                        <input 
                            type="text" 
                            name="username" 
                            value={formData.username} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>

                    {/* Password input */}
                    <div className="form-group">
                        <label>Password:</label>
                        <input 
                            type="password" 
                            name="password" 
                            value={formData.password} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>

                    {/* Confirm Password input */}
                    <div className="form-group">
                        <label>Confirm Password:</label>
                        <input 
                            type="password" 
                            name="confirmPassword" 
                            value={formData.confirmPassword} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>

                    {/* Email input */}
                    <div className="form-group">
                        <label>Email:</label>
                        <input 
                            type="email" 
                            name="email" 
                            value={formData.email} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>

                    {/* Address input fields */}
                    <div className="form-group">
                        <label>Address Line 1:</label>
                        <input 
                            type="text" 
                            name="addressLine1" 
                            value={formData.addressLine1} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label>Address Line 2:</label>
                        <input 
                            type="text" 
                            name="addressLine2" 
                            value={formData.addressLine2} 
                            onChange={handleChange} 
                        />
                    </div>
                    <div className="form-group">
                        <label>City:</label>
                        <input 
                            type="text" 
                            name="city" 
                            value={formData.city} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label>State:</label>
                        <input 
                            type="text" 
                            name="state" 
                            value={formData.state} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>
                    <div className="form-group">
                        <label>Postal Code:</label>
                        <input 
                            type="text" 
                            name="postalCode" 
                            value={formData.postalCode} 
                            onChange={handleChange} 
                            required 
                        />
                    </div>

                    {/* User Type selection */}
                    <div className="form-group">
                        <label>User Type:</label>
                        <select name="type" value={formData.type} onChange={handleChange}>
                            <option value="user">User</option>
                            <option value="librarian">Librarian</option>
                        </select>
                    </div>

                    {/* Submit button */}
                    <button type="submit" className="submit-btn" disabled={loading}>
                        {loading ? 'Signing Up...' : 'Sign Up'}
                    </button>
                </form>

                {/* Error and Success Messages */}
                {errorMessage && <p className="error-message" style={{ color: 'red' }}>{errorMessage}</p>}
                {successMessage && <p className="success-message" style={{ color: 'green' }}>{successMessage}</p>}
            </div>
        </div>
    );
};

export default SignUpForm;
